import{_ as n}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as l,c as i,a as e,e as r,f as o,g as t,h as s}from"./app.382dc3cd.js";const c={},u=o('<h2 id="\u6846\u67B6\u6A21\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u6846\u67B6\u6A21\u5F0F" aria-hidden="true">#</a> \u6846\u67B6\u6A21\u5F0F</h2><h3 id="mvvm-\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#mvvm-\u67B6\u6784" aria-hidden="true">#</a> MVVM \u67B6\u6784</h3><p>M(model)V(view)VM(viewModel)\uFF0CMVVM \u67B6\u6784\u662F MVC \u67B6\u6784\u7684\u6539\u826F\u7248\u672C\u3002</p><p>MVC \u67B6\u6784\u4E0B\u89C6\u56FE\u4E0E\u903B\u8F91\u5206\u79BB\u4F46\u662F\u6570\u636E\u4E0E\u89C6\u56FE\u7684\u6620\u5C04\u903B\u8F91\u9700\u8981\u81EA\u5DF1\u5904\u7406\uFF0C\u6BD4\u5982\u7528 js + html \u5199\u4E00\u4E2A\u5217\u8868\uFF0C\u9700\u8981\u62FF\u5230\u5217\u8868\u6570\u636E\uFF0C\u7528\u5217\u8868\u6570\u636E\u751F\u6210 dom\uFF0C\u7136\u540E\u5C06 dom \u653E\u5230\u9875\u9762\u4E2D\u3002</p><p>MVVM \u67B6\u6784\u4E2D\u5C06\u6839\u636E\u6570\u636E\u751F\u6210 dom \u7684\u64CD\u4F5C\u4EA4\u7ED9\u6846\u67B6\u53BB\u5904\u7406\uFF0C\u524D\u7AEF\u53EA\u9700\u8981\u62FF\u5230\u5217\u8868\u6570\u636E\uFF0C\u7136\u540E\u5C06\u5217\u8868\u6570\u636E\u6309\u7167\u6846\u67B6\u7684\u89C4\u5219\u4E0E dom \u5173\u8054\u5373\u53EF\u3002</p><h2 id="mvvm-vs-virtual-dom" tabindex="-1"><a class="header-anchor" href="#mvvm-vs-virtual-dom" aria-hidden="true">#</a> MVVM vs. Virtual DOM</h2>',6),h=t("\u4EE5\u4E0B\u5185\u5BB9\u6765\u81EA\uFF1A"),d={href:"https://www.zhihu.com/question/31809713/answer/53544875",target:"_blank",rel:"noopener noreferrer"},p=t("\u95EE\u9898\u3010\u7F51\u4E0A\u90FD\u8BF4\u64CD\u4F5C\u771F\u5B9E DOM \u6162\uFF0C\u4F46\u6D4B\u8BD5\u7ED3\u679C\u5374\u6BD4 React \u66F4\u5FEB\uFF0C\u4E3A\u4EC0\u4E48\uFF1F\u3011\u4E2D\u3010\u5C24\u96E8\u6EAA\u3011\u7684\u56DE\u7B54"),M=o('<p>\u76F8\u6BD4\u8D77 React\uFF0C\u5176\u4ED6 MVVM \u7CFB\u6846\u67B6\u6BD4\u5982 Angular, Knockout \u4EE5\u53CA Vue\u3001Avalon \u91C7\u7528\u7684\u90FD\u662F\u6570\u636E\u7ED1\u5B9A\uFF1A\u901A\u8FC7 Directive/Binding \u5BF9\u8C61\uFF0C\u89C2\u5BDF\u6570\u636E\u53D8\u5316\u5E76\u4FDD\u7559\u5BF9\u5B9E\u9645 DOM \u5143\u7D20\u7684\u5F15\u7528\uFF0C\u5F53\u6709\u6570\u636E\u53D8\u5316\u65F6\u8FDB\u884C\u5BF9\u5E94\u7684\u64CD\u4F5C\u3002</p><p>MVVM \u7684\u53D8\u5316\u68C0\u67E5\u662F\u6570\u636E\u5C42\u9762\u7684\uFF0C\u800C React \u7684\u68C0\u67E5\u662F DOM \u7ED3\u6784\u5C42\u9762\u7684\u3002</p><p>MVVM \u7684\u6027\u80FD\u4E5F\u6839\u636E\u53D8\u52A8\u68C0\u6D4B\u7684\u5B9E\u73B0\u539F\u7406\u6709\u6240\u4E0D\u540C\uFF1AAngular \u7684\u810F\u68C0\u67E5\u4F7F\u5F97\u4EFB\u4F55\u53D8\u52A8\u90FD\u6709\u56FA\u5B9A\u7684 <strong>O(watcher count)</strong> \u7684\u4EE3\u4EF7\uFF1BKnockout/Vue/Avalon \u90FD\u91C7\u7528\u4E86\u4F9D\u8D56\u6536\u96C6\uFF0C\u5728 js \u548C DOM \u5C42\u9762\u90FD\u662F <strong>O(change)</strong>\uFF1A</p><ul><li>\u810F\u68C0\u67E5\uFF1Ascope digest <strong>O(watcher count)</strong> + \u5FC5\u8981 DOM \u66F4\u65B0 <strong>O(DOM change)</strong></li><li>\u4F9D\u8D56\u6536\u96C6\uFF1A\u91CD\u65B0\u6536\u96C6\u4F9D\u8D56 <strong>O(data change)</strong> + \u5FC5\u8981 DOM \u66F4\u65B0 <strong>O(DOM change)</strong></li></ul><p>\u53EF\u4EE5\u770B\u5230\uFF0CAngular \u6700\u4E0D\u6548\u7387\u7684\u5730\u65B9\u5728\u4E8E\u4EFB\u4F55\u5C0F\u53D8\u52A8\u90FD\u6709\u548C watcher \u6570\u91CF\u76F8\u5173\u7684\u6027\u80FD\u4EE3\u4EF7\u3002\u4F46\u662F\uFF01\u5F53\u6240\u6709\u6570\u636E\u90FD\u53D8\u4E86\u7684\u65F6\u5019\uFF0CAngular \u5176\u5B9E\u5E76\u4E0D\u5403\u4E8F\u3002\u4F9D\u8D56\u6536\u96C6\u5728\u521D\u59CB\u5316\u548C\u6570\u636E\u53D8\u5316\u7684\u65F6\u5019\u90FD\u9700\u8981\u91CD\u65B0\u6536\u96C6\u4F9D\u8D56\uFF0C\u8FD9\u4E2A\u4EE3\u4EF7\u5728\u5C0F\u91CF\u66F4\u65B0\u7684\u65F6\u5019\u51E0\u4E4E\u53EF\u4EE5\u5FFD\u7565\uFF0C\u4F46\u5728\u6570\u636E\u91CF\u5E9E\u5927\u7684\u65F6\u5019\u4E5F\u4F1A\u4EA7\u751F\u4E00\u5B9A\u7684\u6D88\u8017\u3002</p><p>MVVM \u6E32\u67D3\u5217\u8868\u7684\u65F6\u5019\uFF0C\u7531\u4E8E\u6BCF\u4E00\u884C\u90FD\u6709\u81EA\u5DF1\u7684\u6570\u636E\u4F5C\u7528\u57DF\uFF0C\u6240\u4EE5\u901A\u5E38\u90FD\u662F\u6BCF\u4E00\u884C\u6709\u4E00\u4E2A\u5BF9\u5E94\u7684 ViewModel \u5B9E\u4F8B\uFF0C\u6216\u8005\u662F\u4E00\u4E2A\u7A0D\u5FAE\u8F7B\u91CF\u4E00\u4E9B\u7684\u5229\u7528\u539F\u578B\u7EE7\u627F\u7684 &quot;scope&quot; \u5BF9\u8C61\uFF0C\u4F46\u4E5F\u6709\u4E00\u5B9A\u7684\u4EE3\u4EF7\u3002\u6240\u4EE5\uFF0C<strong>MVVM \u5217\u8868\u6E32\u67D3\u7684\u521D\u59CB\u5316\u51E0\u4E4E\u4E00\u5B9A\u6BD4 React \u6162</strong>\uFF0C\u56E0\u4E3A\u521B\u5EFA ViewModel / scope \u5B9E\u4F8B\u6BD4\u8D77 Virtual DOM \u6765\u8BF4\u8981\u6602\u8D35\u5F88\u591A\u3002</p><p>\u8FD9\u91CC\u6240\u6709 MVVM \u5B9E\u73B0\u7684\u4E00\u4E2A\u5171\u540C\u95EE\u9898\u5C31\u662F\u5728\u5217\u8868\u6E32\u67D3\u7684\u6570\u636E\u6E90\u53D8\u52A8\u65F6\uFF0C\u5C24\u5176\u662F\u5F53\u6570\u636E\u662F\u5168\u65B0\u7684\u5BF9\u8C61\u65F6\uFF0C\u5982\u4F55\u6709\u6548\u5730\u590D\u7528\u5DF2\u7ECF\u521B\u5EFA\u7684 ViewModel \u5B9E\u4F8B\u548C DOM \u5143\u7D20\u3002\u5047\u5982\u6CA1\u6709\u4EFB\u4F55\u590D\u7528\u65B9\u9762\u7684\u4F18\u5316\uFF0C\u7531\u4E8E\u6570\u636E\u662F \u201C\u5168\u65B0\u201D \u7684\uFF0CMVVM \u5B9E\u9645\u4E0A\u9700\u8981\u9500\u6BC1\u4E4B\u524D\u7684\u6240\u6709\u5B9E\u4F8B\uFF0C\u91CD\u65B0\u521B\u5EFA\u6240\u6709\u5B9E\u4F8B\uFF0C\u6700\u540E\u518D\u8FDB\u884C\u4E00\u6B21\u6E32\u67D3\uFF01</p><p>\u76F8\u6BD4\u4E4B\u4E0B\uFF0CReact \u7684\u53D8\u52A8\u68C0\u67E5\u7531\u4E8E\u662F DOM \u7ED3\u6784\u5C42\u9762\u7684\uFF0C\u5373\u4F7F\u662F\u5168\u65B0\u7684\u6570\u636E\uFF0C\u53EA\u8981\u6700\u540E\u6E32\u67D3\u7ED3\u679C\u6CA1\u53D8\uFF0C\u90A3\u4E48\u5C31\u4E0D\u9700\u8981\u505A\u65E0\u7528\u529F\u3002</p><p>\u4E0D\u8981\u5929\u771F\u5730\u4EE5\u4E3A Virtual DOM \u5C31\u662F\u5FEB\uFF0Cdiff \u4E0D\u662F\u514D\u8D39\u7684\uFF0Cbatching \u4E48 MVVM \u4E5F\u80FD\u505A\uFF0C\u800C\u4E14\u6700\u7EC8 patch \u7684\u65F6\u5019\u8FD8\u4E0D\u662F\u8981\u7528\u539F\u751F API\u3002\u5728\u6211\u770B\u6765 Virtual DOM \u771F\u6B63\u7684\u4EF7\u503C\u4ECE\u6765\u90FD\u4E0D\u662F\u6027\u80FD\uFF0C\u800C\u662F\u5B83\uFF1A</p><ol><li>\u4E3A\u51FD\u6570\u5F0F\u7684 UI \u7F16\u7A0B\u65B9\u5F0F\u6253\u5F00\u4E86\u5927\u95E8</li><li>\u53EF\u4EE5\u6E32\u67D3\u5230 DOM \u4EE5\u5916\u7684\u73AF\u5883\uFF0C\u6BD4\u5982 ReactNative</li></ol><h3 id="\u6027\u80FD\u6BD4\u8F83" tabindex="-1"><a class="header-anchor" href="#\u6027\u80FD\u6BD4\u8F83" aria-hidden="true">#</a> \u6027\u80FD\u6BD4\u8F83</h3><ul><li>\u521D\u59CB\u6E32\u67D3\uFF1AVirtual DOM &gt; \u810F\u68C0\u67E5 &gt;= \u4F9D\u8D56\u6536\u96C6</li><li>\u5C0F\u91CF\u6570\u636E\u66F4\u65B0\uFF1A\u4F9D\u8D56\u6536\u96C6 &gt;&gt; Virtual DOM + \u4F18\u5316 &gt; \u810F\u68C0\u67E5\uFF08\u65E0\u6CD5\u4F18\u5316\uFF09 &gt; Virtual DOM \u65E0\u4F18\u5316</li><li>\u5927\u91CF\u6570\u636E\u66F4\u65B0\uFF1A\u810F\u68C0\u67E5 + \u4F18\u5316 &gt;= \u4F9D\u8D56\u6536\u96C6 + \u4F18\u5316 &gt; Virtual DOM\uFF08\u65E0\u6CD5/\u65E0\u9700\u4F18\u5316\uFF09&gt;&gt; MVVM \u65E0\u4F18\u5316</li></ul><h2 id="angular-\u4E0E-vue-\u5BF9\u6BD4" tabindex="-1"><a class="header-anchor" href="#angular-\u4E0E-vue-\u5BF9\u6BD4" aria-hidden="true">#</a> Angular \u4E0E Vue \u5BF9\u6BD4</h2><ol><li>\u4F53\u79EF\u548C\u6027\u80FD</li></ol><p>\u76F8\u8F83\u4E8E vue\uFF0Cangular \u663E\u5F97\u6BD4\u8F83\u81C3\u80BF\uFF0C\u6BD4\u5982\u4E00\u4E2A\u5305\u542B\u4E86 Vuex + Vue Router \u7684 Vue \u9879\u76EE (gzip \u4E4B\u540E 30kB) \uFF0C\u800C angular-cli \u751F\u6210\u7684\u9ED8\u8BA4\u9879\u76EE\u5C3A\u5BF8 (~65KB) \u8FD8\u662F\u8981\u5C0F\u5F97\u591A\u3002</p><p>\u5728\u6E32\u67D3\u6027\u80FD\u4E0A\uFF0C\u8FD9\u4E24\u4E2A\u6846\u67B6\u90FD\u5F88\u5FEB\uFF0C\u6027\u80FD\u4E0A\u51E0\u4E4E\u6CA1\u6709\u5DEE\u522B\u3002</p><ol start="2"><li>\u5F00\u53D1\u6548\u7387</li></ol><p>\u90FD\u63D0\u4F9B\u4E86\u5404\u81EA\u7684\u811A\u624B\u67B6\u5DE5\u5177\uFF0C\u5E2E\u52A9\u63D0\u9AD8\u4ECE\u5F00\u53D1\u5230\u6784\u5EFA\u6253\u5305\u7684\u6574\u5957\u8FC7\u7A0B\uFF0C\u53E6\u5916\u5747\u53EF\u4EE5\u57FA\u4E8E\u7EC4\u4EF6\u5316\u5F00\u53D1\uFF0C\u7F16\u5199\u53EF\u590D\u7528\u7684\u7EC4\u4EF6\uFF0C\u5927\u5927\u8F83\u5C11\u91CD\u590D\u6027\u7684\u5DE5\u4F5C\u3002</p><p>Angular \u662F\u7528 Typescript \u5199\u7684\uFF0C \u66F4\u5177\u6709\u5DE5\u7A0B\u5316\u7684\u4F18\u52BF\uFF0C\u4F46\u662F Vue \u4E5F\u53EF\u4EE5\u901A\u8FC7\u5F15\u5165 TS \u53CA vuex-ts-decorators \u63D2\u4EF6\uFF08\u975E\u5B98\u65B9\u63D2\u4EF6\uFF0C\u5B66\u4E60\u6210\u672C\uFF09\u6765\u62B9\u5E73\u8FD9\u4E00\u5DEE\u8DDD\u3002</p><ol start="3"><li>\u7075\u6D3B\u6027</li></ol><p>Vue \u76F8\u6BD4\u4E8E Angular \u66F4\u52A0\u7075\u6D3B\uFF0C\u53EF\u4EE5\u6309\u7167\u4E0D\u540C\u7684\u9700\u8981\u53BB\u7EC4\u7EC7\u9879\u76EE\u7684\u5E94\u7528\u4EE3\u7801\u3002\u6BD4\u5982\uFF0C\u751A\u81F3\u53EF\u4EE5\u76F4\u63A5\u50CF\u5F15\u7528 jquery \u90A3\u6837\u5728 HTML \u4E2D\u5F15\u7528 vue\uFF0C\u7136\u540E\u4EC5\u4EC5\u5F53\u6210\u4E00\u4E2A\u524D\u7AEF\u7684\u6A21\u677F\u5F15\u64CE\u6765\u7528\u3002</p><ol start="4"><li>\u53EF\u7EF4\u62A4\u6027</li></ol><p>\u76F8\u5BF9\u800C\u8A00 Vue \u5BF9 TS \u7684\u652F\u6301\u5EA6\u8F83\u4F4E\uFF0C\u5DE5\u7A0B\u5316\u65B9\u9762\u4E0D\u5982 Angular\u3002</p><p>Angular \u7684\u6A21\u5757\u7CFB\u7EDF\u53EF\u4EE5\u65B9\u4FBF\u7684\u5BF9\u7EC4\u4EF6\u8FDB\u884C\u7EC4\u88C5\u548C\u89E3\u8026\u3002\u56E0\u6B64 Angular \u66F4\u9002\u5408\u5927\u578B\u9879\u76EE\u7684\u5F00\u53D1\u3002</p><h2 id="\u53C2\u8003" tabindex="-1"><a class="header-anchor" href="#\u53C2\u8003" aria-hidden="true">#</a> \u53C2\u8003</h2>',25),V={href:"https://www.zhihu.com/question/31809713/answer/53544875",target:"_blank",rel:"noopener noreferrer"},g=t("\u95EE\u9898\u3010\u7F51\u4E0A\u90FD\u8BF4\u64CD\u4F5C\u771F\u5B9E DOM \u6162\uFF0C\u4F46\u6D4B\u8BD5\u7ED3\u679C\u5374\u6BD4 React \u66F4\u5FEB\uFF0C\u4E3A\u4EC0\u4E48\uFF1F\u3011\u4E2D\u3010\u5C24\u96E8\u6EAA\u3011\u7684\u56DE\u7B54"),_={href:"https://blog.csdn.net/kwame211/article/details/107284086",target:"_blank",rel:"noopener noreferrer"},m=t("angular \u548C vue \u5BF9\u6BD4");function v(O,f){const a=s("ExternalLinkIcon");return l(),i("div",null,[u,e("p",null,[h,e("a",d,[p,r(a)])]),M,e("p",null,[e("a",V,[g,r(a)])]),e("p",null,[e("a",_,[m,r(a)])])])}const x=n(c,[["render",v],["__file","\u524D\u7AEF\u6846\u67B6\u5BF9\u6BD4.html.vue"]]);export{x as default};
