import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as l,c as t,f as o}from"./app.17eb5f9b.js";const r={},s=o("<ul><li><strong>\u4F60\u7684\u4EA7\u54C1\u662F\u4E00\u79CD\u8D44\u4EA7\uFF0C\u4F46\u4EE3\u7801\u662F\u4E00\u79CD\u8D1F\u503A\u3002</strong></li></ul><p>\u60A8\u7684\u4EA7\u54C1\u89E3\u51B3\u4E86\u5BA2\u6237\u7684\u95EE\u9898\uFF0C\u56E0\u6B64\u662F\u60A8\u7684\u8D44\u4EA7\u3002\u4EE3\u7801\u672C\u8EAB\u5C31\u662F\u521B\u5EFA\u8D44\u4EA7\u7684\u6210\u672C\u3002\u60A8\u62E5\u6709\u7684\u4EE3\u7801\u8D8A\u591A\uFF0C\u5C31\u8D8A\u9700\u8981\u9605\u8BFB\u3001\u6D4B\u8BD5\u3001\u66F4\u6539\u548C\u7406\u89E3\u3002</p><ul><li><strong>\u91CD\u590D\u6BD4\u8FC7\u65E9\u62BD\u8C61\u7684\u6210\u672C\u8981\u4F4E\u3002</strong></li></ul><p>\u9664\u975E\u60A8\u975E\u5E38\u6709\u4FE1\u5FC3\u60A8\u7684\u62BD\u8C61\u80FD\u591F\u4E3A\u81EA\u5DF1\u5E26\u6765\u56DE\u62A5\uFF0C\u56E0\u4E3A\u5B83\u89E3\u51B3\u4E86\u60A8\u786E\u5B9E\u9047\u5230\u7684\u771F\u5B9E\u7684\u62BD\u8C61\u95EE\u9898\uFF0C\u5426\u5219\u4E0D\u8981\u8FD9\u6837\u505A\u3002\u7B49\u5F85\u5E76\u4E86\u89E3\u66F4\u591A\u4FE1\u606F\u3002\u5728\u6B64\u4E4B\u524D\uFF0C\u91CD\u590D\u4EE3\u7801\u53EF\u4EE5\u5E2E\u52A9\u907F\u514D\u4F9D\u8D56\u6027\uFF0C\u8FD9\u672C\u8EAB\u4F7F\u5F97\u4EE3\u7801\u66F4\u5BB9\u6613\u72EC\u7ACB\u66F4\u6539\u6216\u5220\u9664\u3002</p><p>\u8FC7\u65E9\u7684\u62BD\u8C61\u4F1A\u901A\u8FC7\u4F9D\u8D56\u548C\u95F4\u63A5\u9020\u6210\u590D\u6742\u6027\uFF0C\u5E76\u53EF\u80FD\u6210\u4E3A\u60A8\u54CD\u5E94\u53D8\u5316\u7684\u80FD\u529B\u7684\u74F6\u9888\u3002</p><ul><li><strong>\u4EE3\u7801\u5E94\u8BE5\u6613\u4E8E\u5220\u9664\u3002</strong></li></ul><p>\u7F16\u5199\u53EF\u79FB\u52A8\u7684\u4EE3\u7801\uFF0C\u8FD9\u5728\u5F88\u5927\u7A0B\u5EA6\u4E0A\u4E0E\u201C\u89E3\u8026\u201D\u76F8\u540C\u3002\u5F53\u7136\uFF0C\u5E76\u975E\u6240\u6709\u4EE3\u7801\u90FD\u9700\u8981\u7C7B\u4F3C\u5730\u53EF\u5220\u9664\u3002</p><p>\u4F46\u6700\u5927\u9650\u5EA6\u5730\u51CF\u5C11\u4F9D\u8D56\u6027\u3001\u901A\u8FC7\u5B9A\u4E49\u826F\u597D\u7684\u63A5\u53E3\u5177\u6709\u6E05\u6670\u7684\u8FB9\u754C\u4EE5\u53CA\u5468\u5230\u7684\u6574\u4F53\u7CFB\u7EDF\u8BBE\u8BA1\u53EF\u4EE5\u66F4\u8F7B\u677E\u5730\u5220\u9664/\u66F4\u6539\u90E8\u4EF6\u3002</p><p>\u6211\u559C\u6B22\u8FD9\u6837\u7684\u6697\u793A\uFF1A\u5220\u9664\u4EE3\u7801\u53EF\u4EE5\u964D\u4F4E\u672A\u6765\u7684\u6210\u672C\u3002</p><ul><li><strong>\u4E0D\u8981\u7528\u67D0\u4E00\u6BB5\u4EE3\u7801\u7684\u597D\u574F\u6765\u8BC4\u5224\u7A0B\u5E8F\u5458\u7684\u597D\u574F\u3002</strong></li></ul><p>\u4EE3\u7801\u53EA\u662F\u4E00\u4E2A\u65F6\u523B\uFF0C\u5B83\u6355\u83B7\u4E86\u6211\u4EEC\u8BA4\u4E3A\u6211\u4EEC\u4E86\u89E3\u7684\u67D0\u4E9B\u4E8B\u7269\uFF0C\u4E0D\u80FD\u5B8C\u5168\u4EE3\u8868\u4F60\u3002</p><p>\u4F60\u53EF\u80FD\u5DF2\u7ECF\u5199\u4E86\u5B83\uFF0C\u4F46\u4ECE\u90A3\u4E00\u523B\u8D77\uFF08\u5373\u4F7F\u662F 3 \u5206\u949F\u524D\uFF09\u4F60\u5DF2\u7ECF\u6210\u957F\u4E86\uFF0C\u4F46\u4EE3\u7801\u5374\u6CA1\u6709\u3002</p><p>\u5173\u4E8E\u4EE3\u7801\u7684\u8BA8\u8BBA\uFF0C\u65E0\u8BBA\u597D\u574F\uFF0C\u90FD\u4E0D\u5E94\u8BE5\u662F\u79C1\u4EBA\u7684\u3002\u4FDD\u6301\u4E13\u4E1A\u3002\u8C08\u8BBA\u4EE3\u7801\u6216\u95EE\u9898\uFF0C\u4F46\u4E0D\u8981\u8C08\u8BBA\u7F16\u5199\u4EE3\u7801\u7684\u4EBA\u3002</p><ul><li><strong>\u6559\u5B66\u662F\u4E00\u79CD\u53D8\u76F8\u7684\u5B66\u4E60\u5F62\u5F0F\u3002</strong></li></ul><p>\u5982\u679C\u60A8\u8BA4\u4E3A\u81EA\u5DF1\u77E5\u9053\u67D0\u4EF6\u4E8B\uFF0C\u8BF7\u5C1D\u8BD5\u6559\u6388\u5B83\u3002\u901A\u5E38\uFF0C\u8BD5\u56FE\u5411\u522B\u4EBA\u89E3\u91CA\u4F60\u6240\u77E5\u9053\u7684\u4E8B\u60C5\u672C\u8EAB\u5C31\u4F1A\u8FEB\u4F7F\u4F60\u66F4\u6E05\u695A\u5730\u5F62\u5F0F\u5316\u4F60\u81EA\u5DF1\u7684\u60F3\u6CD5\u3002</p><p>\u628A\u4E8B\u60C5\u5199\u4E0B\u6765\u4F3C\u4E4E\u4E5F\u6709\u7C7B\u4F3C\u7684\u6548\u679C\u3002</p><ul><li><strong>\u907F\u514D\u8FC7\u5FEB\u7684\u505A\u51FA\u51B3\u7B56\u3002</strong></li></ul><p>\u6211\u4ECD\u5728\u5B66\u4E60\u8FD9\u4E00\u70B9\uFF0C\u5E76\u52AA\u529B\u907F\u514D\u6211\u51E0\u4E4E\u9ED8\u8BA4\u7684\u5FEB\u901F\u51B3\u5B9A\u7684\u613F\u671B\u3002</p><p>\u4E8B\u5B9E\u662F\uFF0C\u4F60\u63A8\u8FDF\u975E\u5FC5\u8981\u51B3\u7B56\u7684\u65F6\u95F4\u8D8A\u957F\uFF0C\u5728\u505A\u51FA\u51B3\u7B56\u65F6\u9700\u8981\u4F9D\u8D56\u7684\u4FE1\u606F\u5C31\u8D8A\u591A\u3002</p><p>\u5F53\u7136\uFF0C\u4F60\u4E0D\u80FD\u603B\u662F\u62D6\u5EF6\u505A\u51B3\u5B9A\uFF0C\u4F46\u901A\u5E38\u4F60\u53EF\u4EE5\uFF0C\u800C\u4E14\u81F3\u5C11\u4F60\u5E94\u8BE5\u8003\u8651\u73B0\u5728\u4E0D\u77E5\u9053\u7B54\u6848\u662F\u5426\u771F\u7684\u53EF\u4EE5\u3002</p><ul><li><strong>\u575A\u6301\u4F7F\u7528\u65E0\u804A\u7684\u6280\u672F\uFF0C\u9664\u975E\u6709\u5145\u5206\u7684\u7406\u7531\u4E0D\u8FD9\u6837\u505A\u3002</strong></li></ul><p>\u65E0\u804A\u7684\u6280\u672F\u5F80\u5F80\u66F4\u53E4\u8001\uFF0C\u4E5F\u66F4\u5BB9\u6613\u7406\u89E3\u3002</p><p>\u5BF9\u4E8E\u5982\u4F55\u6709\u6548\u5730\u4F7F\u7528\u5B83\uFF0C\u6211\u4EEC\u6709\u4E45\u7ECF\u6C99\u573A\u7684\u7ECF\u9A8C\uFF0C\u53EF\u4EE5\u66F4\u597D\u5730\u7406\u89E3\u5B83\u7684\u6545\u969C\u6A21\u5F0F\uFF0C\u5E76\u4E14\u66F4\u5BB9\u6613\u627E\u5230\u5173\u4E8E\u5982\u4F55\u6700\u597D\u5730\u5E94\u7528\u5B83\u7684\u4EBA\u5458\u548C\u8D44\u6E90\u3002</p><p>\u6211\u771F\u7684\u5F88\u559C\u6B22\u4E39\xB7\u9EA6\u91D1\u5229\u7684\u521B\u65B0\u4EE3\u5E01\u60F3\u6CD5\u3002\u4F60\u53EA\u80FD\u5F97\u5230 3 \u4E2A\u3002\u7528\u5B83\u4EEC\u6765\u91C7\u7528\u6216\u6784\u5EFA\u5168\u65B0\u7684\u4E1C\u897F\u2014\u2014\u6700\u597D\u662F\u80FD\u8BA9\u4F60\u66F4\u597D\u5730\u53D1\u6325\u6838\u5FC3\u80FD\u529B\u7684\u4E1C\u897F\u2014\u2014\u4F46\u4EFB\u4F55\u8D85\u8FC7 3 \u4E2A\uFF0C\u6C38\u8FDC\u65E0\u6CD5\u8FBE\u5230\u7A33\u5B9A/\u6210\u719F\u7684\u98CE\u9669\u5C31\u4F1A\u5F00\u59CB\u589E\u52A0\u3002</p><ul><li><strong>\u5728\u4F60\u989D\u5916\u60F3\u5230\u4E00\u4E2A\u89E3\u51B3\u65B9\u6848\u4E4B\u524D\uFF0C\u4E0D\u8981\u9009\u62E9\u90A3\u4E2A\u4EC5\u6709\u7684\u65B9\u6848\u3002</strong></li></ul><p>\u5F53\u8FD9\u4EF6\u4E8B\u5728\u4F60\u8111\u6D77\u4E2D\u95EA\u73B0\u5E76\u4E14\u4F60\u610F\u8BC6\u5230\u4F60\u627E\u5230\u4E86\u89E3\u51B3\u95EE\u9898\u7684\u65B9\u6CD5\u65F6\uFF0C\u8FD9\u662F\u8BF1\u4EBA\u548C\u4EE4\u4EBA\u5174\u594B\u7684\u3002</p><p>\u4E5F\u8BB8\u5BF9\u4E8E\u4E00\u4E2A\u5FAE\u4E0D\u8DB3\u9053\u7684\u95EE\u9898\u6765\u8BF4\u8FD9\u5F88\u9177\uFF0C\u800C\u4E14\u5B9E\u9645\u4E0A\u6CA1\u6709\u4EC0\u4E48\u53EF\u505A\u7684\u3002\u4F46\u662F\uFF0C\u5982\u679C\u95EE\u9898\u975E\u5E38\u91CD\u8981\u6216\u4E0D\u91CD\u8981\uFF0C\u5219\u503C\u5F97\u8003\u8651\u53EF\u80FD\u8FD8\u6709\u60A8\u5C1A\u672A\u60F3\u5230\u7684\u5176\u4ED6\u89E3\u51B3\u65B9\u6848\u3002</p><ul><li><strong>\u6211\u4EEC\u7684\u4EF7\u503C\u4E0D\u662F\u6211\u4EEC\u4E86\u89E3\u4E00\u5207\u7684\u80FD\u529B\uFF0C\u800C\u662F\u6211\u4EEC\u5B66\u4E60\u3001\u53D1\u73B0\u3001\u56DE\u7B54\u8FD9\u4E9B\u95EE\u9898\u548C\u521B\u9020\u65B0\u95EE\u9898\u7684\u80FD\u529B\u3002</strong></li></ul><p>\u6211\u4EEC\u7684\u884C\u4E1A\u53D1\u5C55\u5F88\u5FEB\uFF0C\u867D\u7136\u6709\u5F88\u591A\u65B0\u7684\u65E7\u4E8B\u7269\uFF0C\u4F46\u4E5F\u6709\u5927\u91CF\u7684\u65B0\u4E8B\u7269\u3002\u6211\u4EEC\u6BCF\u5929\u90FD\u5728\u5B66\u4E60\uFF0C\u6CA1\u6709\u7B54\u6848\u7EDD\u5BF9\u6CA1\u5173\u7CFB\u3002</p><p>\u6211\u4EEC\u7684\u4EF7\u503C\u4E0D\u662F\u6211\u4EEC\u4E86\u89E3\u4E00\u5207\u7684\u80FD\u529B\uFF0C\u800C\u662F\u6211\u4EEC\u5B66\u4E60\u3001\u53D1\u73B0\u3001\u56DE\u7B54\u8FD9\u4E9B\u95EE\u9898\u548C\u521B\u9020\u65B0\u95EE\u9898\u7684\u80FD\u529B\u3002</p><ul><li><strong>\u4E3B\u52A8\u72AF\u9519\u3002</strong></li></ul><p>\u51E0\u6B21\u5B8C\u5168\u9519\u4E86\u7136\u540E\u91CD\u65B0\u5F00\u59CB\u53EF\u80FD\u6BD4\u7B2C\u4E00\u6B21\u5C1D\u8BD5\u505A\u5BF9\u8981\u5FEB\u3002\u6709\u65F6\uFF0C\u63A2\u7D22\u95EE\u9898\u7684\u6700\u4F73\u65B9\u6CD5\u662F\u6DF1\u5165\u7814\u7A76\u95EE\u9898\u5E76\u5C3D\u53EF\u80FD\u591A\u5730\u5C1D\u8BD5\u3002</p><p>\u4E5F\u8BB8\u60A8\u8FD8\u6CA1\u6709\u771F\u6B63\u7406\u89E3\u95EE\u9898\u7A7A\u95F4\uFF0C\u4F46\u662F\u901A\u8FC7\u5C1D\u8BD5\u4E00\u4E9B\u4E8B\u60C5\uFF0C\u60A8\u53EF\u4EE5\u53D1\u73B0\u6709\u5173\u8BBE\u8BA1\u7684\u9AD8\u7EA7\u5BF9\u8BDD\u6216\u9605\u8BFB\u6587\u6863\u4F1A\u5B8C\u5168\u9519\u8FC7\u7684\u4E1C\u897F\u3002\u5982\u679C\u4F60\u53EF\u4EE5\u81EA\u7531\u5730\u72AF\u5C3D\u53EF\u80FD\u591A\u7684\u9519\u8BEF\uFF0C\u5E76\u5728\u6700\u540E\u628A\u5B83\u5168\u90E8\u6254\u6389\uFF0C\u4F60\u53EF\u4EE5\u5B66\u5F97\u5F88\u5FEB\u3002</p><ul><li><strong>\u4E00\u4E2A\u597D\u7684\u8BBE\u8BA1\u662F\u4F60\u53EF\u4EE5\u6539\u53D8\u4E3B\u610F\u800C\u4E0D\u9700\u8981\u6539\u53D8\u592A\u591A\u4EE3\u7801\u7684\u8BBE\u8BA1\u3002</strong></li></ul><p>\u53D8\u5316\u662F\u6052\u5B9A\u7684\u3002\u8FD9\u610F\u5473\u7740\u6211\u4EEC\u9700\u8981\u5F88\u597D\u5730\u5E94\u5BF9\u4E0D\u65AD\u53D8\u5316\u7684\u6761\u4EF6\u624D\u80FD\u53D6\u5F97\u6210\u529F\u2014\u2014\u4E0D\u4EC5\u4EC5\u662F\u53D1\u751F\u5728\u6211\u4EEC\u5468\u56F4\u5E76\u5E26\u9886\u6211\u4EEC\u524D\u8FDB\u7684\u5916\u90E8\u53D8\u5316\uFF0C\u8FD8\u5305\u62EC\u6765\u81EA\u67A2\u8F74\u3001\u65B0\u529F\u80FD\u3001\u6269\u5C55\u6311\u6218\u7B49\u7684\u5185\u90E8\u53D8\u5316\u3002</p><p>\u4E00\u4E2A\u597D\u7684\u7CFB\u7EDF\u8BBE\u8BA1\u5E94\u8BE5\u5C3D\u53EF\u80FD\u5730\u9002\u5E94\u6211\u4EEC\u6539\u53D8\u89E3\u51B3\u95EE\u9898\u65B9\u5F0F\u7684\u9700\u8981\uFF0C\u800C\u4E0D\u5F3A\u8FEB\u6211\u4EEC\u4ECE\u5934\u5F00\u59CB\u3002\u6362\u53E5\u8BDD\u8BF4\uFF0C\u6211\u4EEC\u9700\u8981\u66F4\u6539\u6216\u5220\u9664\u7684\u90E8\u5206\u8D8A\u5C11\uFF0C\u6211\u4EEC\u9762\u5BF9\u53D8\u5316\u7684\u901F\u5EA6\u5C31\u8D8A\u5FEB\uFF1B\u8BBE\u8BA1\u8D8A\u597D\u3002</p>",36),n=[s];function i(u,g){return l(),t("div",null,n)}const c=p(r,[["render",i],["__file","\u6211\u7684\u5DE5\u7A0B\u516C\u7406.html.vue"]]);export{c as default};
