import{_ as i}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as a,c as p,f as e}from"./app.17eb5f9b.js";const l={},h=e('<h2 id="\u6982\u8FF0" tabindex="-1"><a class="header-anchor" href="#\u6982\u8FF0" aria-hidden="true">#</a> \u6982\u8FF0</h2><p>\u4E0A\u5377\u4ECB\u7ECD\u4E86\u73B0\u4EE3\u4EBA\u5DE5\u667A\u80FD\u662F\u4EC0\u4E48\uFF0C\u4E0B\u5377\u4ECB\u7ECD\u4E86\u793E\u4F1A\u8FC8\u5411\u667A\u80FD\u65F6\u4EE3\u6240\u4F9D\u8D56\u7684\u6280\u672F\u53CA\u4EBA\u5DE5\u667A\u80FD\uFF08\u5927\u6570\u636E+\u673A\u5668\u667A\u80FD\uFF09\u5BF9\u73B0\u6709\u4EA7\u4E1A\u7684\u5F71\u54CD\u3002</p><p>\u4E0A\u5377\u4E2D\u5BF9\u4EBA\u5DE5\u667A\u80FD\u7684\u4ECB\u7ECD\u5F88\u8BE6\u7EC6\uFF0C\u4ECE\u6570\u636E\u3001\u673A\u5668\u667A\u80FD\u7684\u53D1\u5C55\u5F00\u59CB\u8BB2\u8FF0\u73B0\u4EE3\u4EBA\u5DE5\u667A\u80FD\u7684\u672C\u8D28\uFF1A\u5BF9\u6240\u6709\u53EF\u80FD\u6027\u7684\u7A77\u4E3E\uFF0C\u800C\u4E0D\u662F\u8BA9\u673A\u5668\u50CF\u4EBA\u4E00\u6837\u601D\u8003\u3002\u8FD9\u9700\u8981\u8BA1\u7B97\u673A\u80FD\u591F\u5904\u7406\u6D77\u91CF\u6570\u636E\u540C\u65F6\u5728\u6742\u4E71\u7684\u6570\u636E\u4E2D\u627E\u5230\u6570\u636E\u7684\u5173\u8054\u6027\u5C06\u5176\u6574\u7406\u6210\u6709\u7528\u7684\u4FE1\u606F\u3002</p><p>\u4E0B\u5377\u4E2D\u4ECB\u7ECD\u4E86\u4E00\u4E9B\u70ED\u95E8\u7684\u6280\u672F\uFF0C\u5982\uFF1A\u7269\u8054\u7F51\u30015G\u3001\u533A\u5757\u94FE\uFF0C\u4F46\u662F\u53EA\u8BB2\u4E86\u4ED6\u4EEC\u5BF9\u4E8E\u667A\u80FD\u65F6\u4EE3\u7684\u4F5C\u7528\u548C\u610F\u4E49\uFF0C\u6280\u672F\u672C\u8EAB\u5E76\u6CA1\u6709\u6DF1\u5165\u3002\u8BB2\u8FF0\u4E86\u672A\u6765\u7684\u4EA7\u4E1A\u548C\u672A\u6765\u793E\u4F1A\uFF0C\u7ED9\u4EBA\u5F88\u6DF1\u7684\u7126\u8651\u611F\uFF0C\u5BF9\u4E8E\u5982\u4F55\u6210\u4E3A 2% \u7684\u4EBA\u53EA\u7ED9\u51FA\u4E00\u53E5\u8BDD\uFF1A\u5229\u7528\u597D\u5927\u6570\u636E\u548C\u673A\u5668\u667A\u80FD\u3002\u5E76\u5217\u4E3E\u4E86\u5546\u573A\u548C\u9152\u5427\u5229\u7528\u8FD9\u4E9B\u6280\u672F\u63D0\u9AD8\u8425\u4E1A\u989D\u7684\u4F8B\u5B50\u3002</p><p>\u770B\u4E86\u672C\u4E66\u53EF\u4EE5\u5BF9\u4EBA\u5DE5\u667A\u80FD\u6709\u66F4\u6DF1\u5165\u7684\u4E86\u89E3\u800C\u4E0D\u53EA\u662F\u6D6E\u4E8E\u8868\u9762\uFF0C\u4E5F\u53EF\u4EE5\u770B\u5230\u4EBA\u5DE5\u667A\u80FD\u5BF9\u793E\u4F1A\u6B63\u5728\u4EA7\u751F\u7684\u5F71\u54CD\u3002</p><h2 id="\u6570\u636E\u3001\u4FE1\u606F\u548C\u77E5\u8BC6" tabindex="-1"><a class="header-anchor" href="#\u6570\u636E\u3001\u4FE1\u606F\u548C\u77E5\u8BC6" aria-hidden="true">#</a> \u6570\u636E\u3001\u4FE1\u606F\u548C\u77E5\u8BC6</h2><ol><li><p>\u968F\u7740\u4EBA\u7C7B\u7684\u8FDB\u6B65\u4EE5\u53CA\u5904\u7406\u6570\u636E\u548C\u4FE1\u606F\u7684\u80FD\u529B\u4E0D\u65AD\u589E\u5F3A\uFF0C\u4EBA\u7C7B\u4ECE\u6570\u636E\u4E2D\u83B7\u53D6\u6709\u7528\u4FE1\u606F\u7684\u80FD\u529B\u8D8A\u6765\u8D8A\u5F3A\uFF0C\u8FD9\u5C31\u662F\u4ECA\u5929\u6240\u8BF4\u7684\u5927\u6570\u636E\u5E94\u7528\u7684\u57FA\u7840\u3002</p></li><li><p>\u6570\u636E\u662F\u6587\u660E\u7684\u57FA\u77F3\uFF0C\u4EBA\u7C7B\u5BF9\u5B83\u7684\u8BA4\u8BC6\u53CD\u6620\u4E86\u6587\u660E\u7684\u7A0B\u5EA6\u3002</p></li><li><p>\u5BF9\u6570\u636E\u548C\u4FE1\u606F\u8FDB\u884C\u5904\u7406\u540E\uFF0C\u4EBA\u7C7B\u5C31\u53EF\u4EE5\u83B7\u5F97\u77E5\u8BC6\u3002\u6BD4\u5982\uFF0C\u901A\u8FC7\u6D4B\u91CF\u661F\u7403\u7684\u4F4D\u7F6E\u548C\u5BF9\u5E94\u7684\u65F6\u95F4\uFF0C\u5C31\u5F97\u5230\u4E86\u6570\u636E\uFF1B\u901A\u8FC7\u8FD9\u4E9B\u6570\u636E\u5F97\u5230\u661F\u7403\u8FD0\u52A8\u7684\u8F68\u8FF9\uFF0C\u5C31\u662F\u4FE1\u606F\uFF1B\u901A\u8FC7\u4FE1\u606F\u603B\u7ED3\u51FA\u5F00\u666E\u52D2\u4E09\u5B9A\u5F8B\uFF0C\u5C31\u662F\u77E5\u8BC6\u3002</p></li><li><p>\u4EBA\u7C7B\u7684\u8FDB\u6B65\u5C31\u662F\u9760\u4F7F\u7528\u77E5\u8BC6\u4E0D\u65AD\u5730\u6539\u53D8\u6211\u4EEC\u7684\u751F\u6D3B\u548C\u5468\u56F4\u7684\u4E16\u754C\uFF0C\u800C\u6570\u636E\u662F\u77E5\u8BC6\u7684\u57FA\u7840\u3002</p></li><li><p>\u5386\u6CD5\u5B9E\u9645\u4E0A\u5C31\u662F\u5BF9\u5929\u6587\u73B0\u8C61\u7684\u4E00\u4E2A\u6570\u636E\u5316\u63CF\u8FF0\u3002</p></li><li><p>\u83B7\u53D6\u77E5\u8BC6\u7684\u56DB\u4E2A\u9636\u6BB5\uFF1A\u83B7\u53D6\u6570\u636E ---&gt; \u5206\u6790\u6570\u636E ---&gt; \u5EFA\u7ACB\u6A21\u578B ---&gt; \u9884\u6D4B\u672A\u77E5</p></li><li><p>\u6570\u636E\u548C\u6240\u60F3\u83B7\u5F97\u7684\u4FE1\u606F\u4E4B\u95F4\u7684\u8054\u7CFB\u901A\u5E38\u662F\u95F4\u63A5\u7684\uFF0C\u5B83\u9700\u8981\u901A\u8FC7\u4E0D\u540C\u6570\u636E\u4E4B\u95F4\u7684\u76F8\u5173\u6027\u624D\u80FD\u4F53\u73B0\u51FA\u6765\u3002</p></li><li><p>\u6709\u6548\u7684\u7EDF\u8BA1\u6570\u636E\u6709\u4E24\u4E2A\u8981\u70B9\uFF1A\u5927\u89C4\u6A21\u7684\u6570\u636E\u6837\u672C\u53CA\u6837\u672C\u5177\u6709\u4EE3\u8868\u6027\u3002</p></li><li><p>\u5728\u79D1\u5B66\u4E0A\uFF0C\u63D0\u51FA\u597D\u7684\u95EE\u9898\u751A\u81F3\u6BD4\u89E3\u51B3\u95EE\u9898\u66F4\u91CD\u8981\uFF08\u8D70\u5728\u6B63\u786E\u7684\u9053\u8DEF\u4E0A\uFF1F\uFF09\u3002</p></li></ol><h2 id="\u5927\u6570\u636E\u7684\u7279\u5F81" tabindex="-1"><a class="header-anchor" href="#\u5927\u6570\u636E\u7684\u7279\u5F81" aria-hidden="true">#</a> \u5927\u6570\u636E\u7684\u7279\u5F81</h2><ol><li>\u4F53\u91CF\u5927</li><li>\u5168\u9762\u6027\u3001\u5B8C\u5907\u6027</li><li>\u591A\u7EF4\u5EA6\uFF1A\u65F6\u95F4\u3001\u5730\u533A\u3001\u5E74\u9F84\u7B49</li></ol><h2 id="\u73B0\u4EE3\u4EBA\u5DE5\u667A\u80FD\u7684\u672C\u8D28" tabindex="-1"><a class="header-anchor" href="#\u73B0\u4EE3\u4EBA\u5DE5\u667A\u80FD\u7684\u672C\u8D28" aria-hidden="true">#</a> \u73B0\u4EE3\u4EBA\u5DE5\u667A\u80FD\u7684\u672C\u8D28</h2><p>\u4F20\u7EDF\u4EBA\u5DE5\u667A\u80FD\u7684\u7814\u7A76\u65B9\u5411\u662F\uFF1A\u8BA9\u673A\u5668\u50CF\u4EBA\u4E00\u6837\u601D\u8003\uFF0C\u8FD9\u5C31\u9700\u8981\u673A\u5668\u5177\u6709\u4EBA\u7684\u601D\u7EF4\u3002\u8FD9\u5C31\u50CF\u65E9\u671F\u7684\u98DE\u884C\u5668\u90FD\u662F\u6A21\u4EFF\u9E1F\u7C7B\u9707\u52A8\u7FC5\u8180\u4E00\u6837\u3002</p><p>\u73B0\u4EE3\u4EBA\u5DE5\u667A\u80FD\u662F\u4F9D\u9760\u5927\u6570\u636E\u7684\u652F\u6491\uFF0C\u4ECE\u800C\u505A\u51FA\u770B\u8D77\u6765\u50CF\u662F\u201C\u4EBA\u7684\u884C\u4E3A\u201D\u7684\u51B3\u7B56\u3002\u628A\u673A\u5668\u667A\u80FD\u7684\u95EE\u9898\u53D8\u6210\u4E86\u5927\u6570\u636E\u7684\u95EE\u9898\u548C\u5927\u91CF\u8BA1\u7B97\u7684\u95EE\u9898\u3002</p><p>\u4ECA\u5929\u4EBA\u5DE5\u667A\u80FD\u91C7\u7528\u7684\u65B9\u6CD5\u548C\u4EBA\u6CE8\u91CD\u903B\u8F91\u63A8\u7406\u7684\u601D\u7EF4\u65B9\u6CD5\u4E0D\u540C\uFF0C\u5B83\u662F\u5229\u7528\u5927\u6570\u636E\uFF0C\u4ECE\u6570\u636E\u4E2D\u5FC3\u5B66\u4E60\u83B7\u5F97\u4FE1\u606F\u548C\u77E5\u8BC6\uFF0C\u7136\u540E\u518D\u5E94\u7528\u5230\u5B9E\u9645\u95EE\u9898\u4E2D\u3002</p><p>\u6240\u4EE5\u8BF4\u4ECA\u5929\u7684\u6240\u8C13\u4EBA\u5DE5\u667A\u80FD\u4E0D\u8FC7\u662F\u4E00\u79CD\u5BF9\u6240\u6709\u53EF\u80FD\u6027\u7684\u7A77\u4E3E\uFF1F</p><h2 id="\u673A\u68B0\u601D\u7EF4\u7684\u672C\u8D28" tabindex="-1"><a class="header-anchor" href="#\u673A\u68B0\u601D\u7EF4\u7684\u672C\u8D28" aria-hidden="true">#</a> \u673A\u68B0\u601D\u7EF4\u7684\u672C\u8D28</h2><p>\u725B\u987F\u544A\u8BC9\u4E16\u4EBA\uFF0C\u4EFB\u4F55\u6B63\u786E\u7684\u7406\u8BBA\u4ECE\u5F62\u5F0F\u4E0A\u8BB2\u90FD\u662F\u7B80\u5355\u7684\uFF0C\u4ECE\u9002\u7528\u8303\u56F4\u4E0A\u8BB2\u662F\u901A\u7528\u7684\u3001\u65E0\u6761\u4EF6\u7684\u3002\u4E00\u4E2A\u88AB\u9A8C\u8BC1\u7684\u7406\u8BBA\uFF0C\u4F60\u5728\u5176\u4ED6\u4EFB\u4F55\u7B26\u5408\u6761\u4EF6\u7684\u5730\u65B9\u9002\u7528\uFF0C\u90FD\u80FD\u5F97\u5230\u6B63\u786E\u7684\u7ED3\u8BBA\u3002</p><p>\u673A\u68B0\u8BBA\u7684\u4E00\u4E2A\u663E\u8457\u7279\u70B9\u662F\u5F3A\u8C03\u4E16\u754C\u7684\u8FDE\u7EED\u6027\uFF0C\u5FFD\u7565\u4E0D\u8FDE\u7EED\u6027\u3002\u8FD9\u5728\u5F53\u65F6\u662F\u6CA1\u6709\u95EE\u9898\u7684\uFF0C\u56E0\u4E3A\u4E00\u65B9\u9762\u5F53\u65F6\u4EBA\u4EEC\u8BA4\u8BC6\u7684\u4E16\u754C\u4E0D\u8DB3\u591F\u5C0F\uFF0C\u4E5F\u4E0D\u8DB3\u591F\u5927\uFF0C\u770B\u4E0D\u5230\u4E0D\u8FDE\u7EED\u6027\uFF1B\u53E6\u4E00\u65B9\u9762\uFF0C\u4E16\u754C\u53D8\u5316\u4E5F\u6BD4\u8F83\u6162\uFF0C\u8DF3\u8DC3\u53D8\u5316\u5E76\u975E\u5E38\u6001\u3002</p><h2 id="\u673A\u68B0\u601D\u7EF4\u7684\u6838\u5FC3\u601D\u60F3" tabindex="-1"><a class="header-anchor" href="#\u673A\u68B0\u601D\u7EF4\u7684\u6838\u5FC3\u601D\u60F3" aria-hidden="true">#</a> \u673A\u68B0\u601D\u7EF4\u7684\u6838\u5FC3\u601D\u60F3</h2><p>\u4E16\u754C\u662F\u8FDE\u7EED\u53D8\u5316\u7684\uFF0C\u800C\u5404\u79CD\u53D8\u5316\u7684\u89C4\u5F8B\u662F\u786E\u5B9A\u7684\u3002</p><p>\u56E0\u4E3A\u6709\u786E\u5B9A\u6027\u505A\u4FDD\u969C\uFF0C\u56E0\u6B64\u89C4\u5F8B\u4E0D\u4EC5\u662F\u53EF\u4EE5\u88AB\u8BA4\u8BC6\u7684\uFF0C\u800C\u4E14\u53EF\u4EE5\u7528\u7B80\u5355\u7684\u516C\u5F0F\u6216\u8BED\u8A00\u63CF\u8FF0\u6E05\u695A\u3002\u5728\u8FD9\u4E4B\u524D\u89C4\u5F8B\u88AB\u5F52\u7ED3\u4E3A\u795E\u7684\u4F5C\u7528\u3002</p><p>\u8FD9\u4E9B\u89C4\u5F8B\u5E94\u8BE5\u662F\u653E\u4E4B\u56DB\u6D77\u800C\u7686\u51C6\u7684\uFF0C\u53EF\u4EE5\u5E94\u7528\u5230\u5404\u79CD\u4F4D\u7F6E\u9886\u57DF\u6307\u5BFC\u5B9E\u8DF5\u3002</p><h2 id="\u4E16\u754C\u7684\u4E0D\u786E\u5B9A\u6027" tabindex="-1"><a class="header-anchor" href="#\u4E16\u754C\u7684\u4E0D\u786E\u5B9A\u6027" aria-hidden="true">#</a> \u4E16\u754C\u7684\u4E0D\u786E\u5B9A\u6027</h2><p>\u5F53\u6211\u4EEC\u8981\u5BF9\u4F4D\u7F6E\u7684\u4E8B\u4EF6\u5BFB\u627E\u4E00\u4E2A\u6982\u7387\u6A21\u578B\u65F6\uFF0C\u8FD9\u4E2A\u6A21\u578B\u5E94\u5F53\u6EE1\u8DB3\u6211\u4EEC\u6240\u6709\u5DF2\u7ECF\u770B\u5230\u7684\u6570\u636E\uFF0C\u4F46\u662F\u5BF9\u672A\u77E5\u7684\u60C5\u51B5\u4E0D\u8981\u505A\u4EFB\u4F55\u4E3B\u89C2\u5047\u8BBE\u3002</p><p>\u5C06\u6700\u597D\u7684\u8D44\u6E90\u7528\u5728\u51FA\u73B0\u9891\u7387\u6700\u9AD8\u7684\u5730\u65B9\u3002</p><p>\u7528\u4E0D\u786E\u5B9A\u6027\u7684\u773C\u5149\u770B\u5F85\u4E16\u754C\uFF0C\u518D\u7528\u4FE1\u606F\u6D88\u9664\u4E0D\u786E\u5B9A\u6027\u3002\u80FD\u591F\u628A\u5F88\u591A\u667A\u80FD\u95EE\u9898\u8F6C\u5316\u6210\u4FE1\u606F\u5904\u7406\u95EE\u9898\u3002\u5927\u6570\u636E\u80FD\u89E3\u51B3\u667A\u80FD\u95EE\u9898\u662F\u56E0\u4E3A\u6076\u5F88\u591A\u667A\u80FD\u95EE\u9898\u4ECE\u6839\u672C\u4E0A\u8BB2\u662F\u6D88\u9664\u4E0D\u786E\u5B9A\u6027\u7684\u95EE\u9898\u3002</p><p>\u4FE1\u606F\u7684\u4F20\u64AD\u901F\u5EA6\u4E0D\u53EF\u80FD\u8D85\u8FC7\u4FE1\u9053\u7684\u5BB9\u91CF\uFF0C\u4E92\u8054\u7F51\u53D1\u5C55\u7684\u5404\u4E2A\u9636\u6BB5\u5B9E\u9645\u4E0A\u662F\u5EFA\u7ACB\u5728\u4E0D\u65AD\u62D3\u5BBD\u5E26\u5BBD\u7684\u57FA\u7840\u4E0A\u7684\u3002</p><h2 id="\u4ECA\u65E5\u5934\u6761\u9884\u6D4B\u7528\u6237\u559C\u597D\u7684\u65B9\u6CD5-\u534F\u540C\u8FC7\u6EE4" tabindex="-1"><a class="header-anchor" href="#\u4ECA\u65E5\u5934\u6761\u9884\u6D4B\u7528\u6237\u559C\u597D\u7684\u65B9\u6CD5-\u534F\u540C\u8FC7\u6EE4" aria-hidden="true">#</a> \u4ECA\u65E5\u5934\u6761\u9884\u6D4B\u7528\u6237\u559C\u597D\u7684\u65B9\u6CD5\uFF1A\u534F\u540C\u8FC7\u6EE4</h2><p>\u5728\u4E2A\u4EBA\u4FE1\u606F\u4E0D\u5B8C\u5168\u7684\u60C5\u51B5\u4E0B\uFF0C\u501F\u7528\u4ED6\u4EBA\u7684\u5DF2\u77E5\u6570\u636E\u8FDB\u884C\u9884\u6D4B\u3002</p><p>\u4ECA\u65E5\u5934\u6761\u5173\u4E8E\u7528\u6237\u7684\u5927\u90E8\u5206\u6570\u636E\u90FD\u662F\u4E0D\u5168\u9762\u7684\uFF0C\u4F46\u662F\u5B83\u7684\u7EAC\u5EA6\u975E\u5E38\u591A\uFF0C\u901A\u8FC7\u5BF9\u4E0D\u540C\u4EBA\u591A\u4E2A\u7EAC \u5EA6\u559C\u597D\u7684\u5BF9\u6BD4\uFF0C\u80FD\u591F\u627E\u5230\u884C\u4E3A\u548C\u504F\u597D\u7C7B\u4F3C\u7684\u4EBA\uFF1B\u518D\u901A\u8FC7\u54EA\u4E9B\u7C7B\u4F3C\u7684\u4EBA\u8BB2\u786E\u5B9E\u7684\u4FE1\u606F\u8865\u56DE\u6765\uFF0C\u8FD9\u5C31\u662F\u5229\u7528\u534F\u540C\u8FC7\u6EE4\u8FDB\u884C\u9884\u6D4B</p><h2 id="\u5927\u6570\u636E\u65F6\u4EE3\u7684\u6570\u636E\u5B89\u5168" tabindex="-1"><a class="header-anchor" href="#\u5927\u6570\u636E\u65F6\u4EE3\u7684\u6570\u636E\u5B89\u5168" aria-hidden="true">#</a> \u5927\u6570\u636E\u65F6\u4EE3\u7684\u6570\u636E\u5B89\u5168</h2><p>\u5728\u5927\u6570\u636E\u65F6\u4EE3\uFF0C\u6570\u636E\u5B89\u5168\u6F0F\u94DC\u5BFC\u81F4\u7684\u5371\u5BB3\u8FDC\u5927\u4E8E\u8FC7\u53BB\u3002\u5728\u8FC7\u53BB\uFF0C\u4FE1\u606F\u5173\u8054\u6027\u3001\u5B8C\u5907\u6027\u90FD\u8F83\u5C0F\uFF0C\u5E76\u4E14\u6570\u636E\u90FD\u662F\u5206\u6563\u5B58\u50A8\uFF08\u4E3A\u4E86\u5B89\u5168\u6027\u5EFA\u9020\u591A\u4E2A\u6570\u636E\u4E2D\u5FC3\u5907\u4EFD\u6570\u636E\uFF09\uFF0C\u5373\u4F7F\u6570\u636E\u88AB\u76D7\u635F\u5931\u4E5F\u662F\u53EF\u9884\u89C1\u7684\uFF1B\u4F46\u662F\u5728\u5927\u6570\u636E\u65F6\u4EE3\u6570\u636E\u90FD\u5B58\u653E\u5728\u4E91\u7AEF\uFF0C\u6570\u636E\u88AB\u76D7\u5C31\u662F\u88AB\u4E00\u9505\u7AEF\u3002</p><p>\u6570\u636E\u5B89\u5168\u9690\u60A3\u5E38\u5E38\u662F\u56E0\u4E3A\u4EBA\u4EEC\u4E0D\u9075\u5B88\u89C4\u8303\uFF0C\u6709\u65F6\u4E3A\u4E86\u5B89\u5168\u800C\u8BBE\u8BA1\u7684\u63AA\u65BD\uFF08\u767B\u5F55\u4E24\u5206\u949F\u5931\u6548\uFF09\uFF0C\u4F1A\u56E0\u4E3A\u64CD\u4F5C\u8005\u4E3A\u4E86\u4FBF\u5229\uFF08\u7531\u4E8E\u5BC6\u7801\u590D\u6742\u5C06\u5BC6\u7801\u8BB0\u5F55\u5728\u5176\u4ED6\u5730\u65B9\uFF09\u800C\u5931\u53BB\u6548\u679C\u3002</p><p>\u5728\u5927\u6570\u636E\u65F6\u4EE3\u6709\u4E00\u79CD\u6709\u6548\u7684\u6570\u636E\u5B89\u5168\u9632\u62A4\u63AA\u65BD\uFF1A\u6839\u636E\u5927\u6570\u636E\u5206\u6790\u7CFB\u7EDF\u6B63\u5E38\u64CD\u4F5C\u7684\u5E38\u89C4\u6D41\u7A0B\uFF0C\u7136\u540E\u6839\u636E\u767B\u5F55\u4EBA\u7684\u64CD\u4F5C\u662F\u5426\u7B26\u5408\u5E38\u89C4\u6D41\u7A0B\u6765\u5224\u65AD\u5F53\u524D\u64CD\u4F5C\u4EBA\u662F\u5408\u6CD5\u7684\u8FD8\u662F\u9ED1\u5BA2\u3002</p><h2 id="\u8D85\u7EA7\u667A\u80FD\u65F6\u4EE3" tabindex="-1"><a class="header-anchor" href="#\u8D85\u7EA7\u667A\u80FD\u65F6\u4EE3" aria-hidden="true">#</a> \u8D85\u7EA7\u667A\u80FD\u65F6\u4EE3</h2><p>\u4EBA\u5DE5\u667A\u80FD + loT = \u8D85\u7EA7\u667A\u80FD\uFF0C\u5982\u679C\u628A\u8D85\u7EA7\u667A\u80FD\u5BF9\u5E94\u4E8E\u4EBA\uFF0C\u90A3\u4E48\u4EBA\u5DE5\u667A\u80FD\u662F\u5927\u8111\uFF0CloT\u5C31\u662F\u795E\u7ECF\u7CFB\u7EDF\uFF0CloT\u4E2D\u7684\u5927\u91CF\u4F20\u611F\u5668\u548C\u8BBE\u5907\u626E\u6F14\u7740\u4F17\u591A\u611F\u5B98\u7EC6\u80DE\u7684\u89D2\u8272\uFF0C\u800C\u6B63\u5728\u53D1\u5C55\u8D77\u6765\u7684 5G \u79FB\u52A8\u901A\u4FE1\u7F51\u7EDC\u5219\u76F8\u5F53\u4E8E\u5468\u56F4\u795E\u7ECF\uFF0C\u533A\u5757\u94FE\u626E\u6F14\u7740\u627F\u8F7D\u751F\u7269\u4FE1\u53F7\u7684\u89D2\u8272\u3002</p><p>\u5728\u672A\u6765\u7684\u8D85\u7EA7\u667A\u80FD\u793E\u4F1A\u91CC\uFF0C\u6709\u56DB\u4EF6\u8FC7\u53BB\u5B8C\u5168\u4E0D\u53EF\u80FD\u529E\u5230\u7684\u4E8B\u60C5\u5F97\u4EE5\u5B9E\u73B0\uFF0C\u4EFB\u4F55\u6709\u52A9\u4E8E\u5B9E\u73B0\u8FD9\u56DB\u4EF6\u4E8B\u60C5\u7684\u6280\u672F\u90FD\u80FD\u6709\u5DE8\u5927\u7684\u5E02\u573A\u3002</p><ol><li>\u4EBA\u7C7B\u80FD\u591F\u66F4\u597D\u5730\u4E86\u89E3\u81EA\u5DF1</li></ol><p>\u4E00\u4E9B\u516C\u53F8\u901A\u5E38\u4F1A\u7ED9\u6839\u636E\u7528\u6237\u4EE5\u5F80\u7684\u884C\u4E3A\u7ED9\u7528\u6237\u753B\u50CF\uFF0C\u5728\u672A\u6765\u6709\u4E86\u66F4\u591A\u3001\u66F4\u5168\u9762\u7684\u6570\u636E\u4E4B\u540E\u8FD9\u4E2A\u7528\u6237\u753B\u50CF\u4F1A\u66F4\u52A0\u5B8C\u5907\u3002</p><ol start="2"><li>\u89E3\u51B3\u5546\u4E1A\u7EA0\u7EB7</li></ol><p>\u5546\u4E1A\u7EA0\u7EB7\u4E3B\u8981\u662F\u56E0\u4E3A\u8FDD\u7EA6\uFF0C\u5728\u672A\u6765\u53EF\u4EE5\u7528\u533A\u5757\u94FE\u6280\u672F\u6765\u660E\u786E\u6BCF\u4E00\u6B65\u5546\u4E1A\u5408\u4F5C\u7684\u5BF9\u8C61\uFF08\u4EE5\u524D\u5982\u679C\u5B58\u5728\u4E2D\u95F4\u4EBA\u5C31\u65E0\u6CD5\u660E\u786E\u8FD9\u70B9\uFF09\uFF0C\u8FD9\u6837\u5C31\u53EF\u4EE5\u660E\u786E\u77E5\u9053\u81EA\u5DF1\u771F\u6B63\u5408\u4F5C\u7684\u5BF9\u8C61\u662F\u8C01\uFF0C\u4ECE\u800C\u6839\u636E\u5BF9\u65B9\u7684\u4FE1\u7528\u6765\u51B3\u5B9A\u8981\u4E0D\u8981\u5408\u4F5C\u3002</p><ol start="3"><li>\u63D0\u9AD8\u793E\u4F1A\u8FD0\u884C\u6548\u7387</li></ol><ul><li>\u793E\u4F1A\u5B89\u5168\uFF1A\u901A\u8FC7\u4EBA\u8138\u8BC6\u522B\u6280\u672F\u6293\u6355\u7F6A\u72AF</li><li>\u53CD\u6B3A\u8BC8\uFF1A\u7528\u5927\u6570\u636E\u591A\u7EF4\u5EA6\u7684\u7279\u70B9\u786E\u5B9A\u662F\u5426\u4E3A\u7528\u6237\u672C\u4EBA\uFF08\u9632\u6B62\u76D7\u5237\u94F6\u884C\u5361\uFF09</li><li>\u4FBF\u5229\u6027\uFF1A\u6709\u4E86\u591A\u7EF4\u5EA6\u7684\u5927\u6570\u636E\uFF0C\u4F01\u4E1A\u53EF\u4EE5\u5728\u53D6\u5F97\u7528\u6237\u4E00\u90E8\u5206\u4FE1\u606F\u65F6\u83B7\u53D6\u53E6\u4E00\u90E8\u5206\u4FE1\u606F\uFF0C\u8BA9\u7528\u6237\u51CF\u5C11\u64CD\u4F5C</li></ul><ol start="4"><li>\u628A\u4EBA\u4ECE\u91CD\u590D\u6027\u7684\u5DE5\u4F5C\u4E2D\u89E3\u653E\u51FA\u6765</li></ol><p>\u5F88\u591A\u51B3\u7B56\uFF0C\u6BD4\u5982\u9A7E\u9A76\u6C7D\u8F66\uFF0C\u8BA1\u7B97\u673A\u4F1A\u6BD4\u6211\u4EEC\u7EDD\u5927\u591A\u6570\u4EBA\u505A\u5F97\u66F4\u597D\u3002</p><p>\u5728\u672A\u6765\uFF0C\u4EBA\u6240\u5173\u6CE8\u7684\u5BF9\u8C61\u4F1A\u53D1\u751F\u53D8\u5316\uFF0C\u4E0D\u9700\u8981\u518D\u53BB\u5173\u6CE8\u90A3\u4E9B\u53EA\u9700\u8981\u7B80\u5355\u667A\u529B\u7684\u4E8B\u60C5\u3002\u8FD9\u6837\u5C31\u6709\u66F4\u591A\u7684\u7CBE\u529B\u53BB\u5173\u6CE8\u9700\u8981\u6DF1\u5165\u601D\u8003\u7684\u4E8B\u60C5\u3002</p><h2 id="\u6280\u672F\u9769\u547D\u7684\u5F71\u54CD" tabindex="-1"><a class="header-anchor" href="#\u6280\u672F\u9769\u547D\u7684\u5F71\u54CD" aria-hidden="true">#</a> \u6280\u672F\u9769\u547D\u7684\u5F71\u54CD</h2><p>\u6280\u672F\u9769\u547D\u7684\u521D\u671F\u4F1A\u5BFC\u81F4\u8D2B\u5BCC\u5DEE\u8DDD\u52A0\u5267\u3002</p><p>\u6280\u672F\u9769\u547D\u4F1A\u6D88\u706D\u5F88\u591A\u4EA7\u4E1A\uFF0C\u8FD9\u4E9B\u4EA7\u4E1A\u7684\u4ECE\u4E1A\u4EBA\u5458\u4E0D\u5F97\u4E0D\u5BFB\u627E\u522B\u7684\u51FA\u8DEF\u3002</p><p>\u667A\u80FD\u65F6\u4EE3\u4EBA\u7C7B\u88AB\u66FF\u4EE3\u7684\u662F\u5927\u8111\uFF0C\u4EBA\u7C7B\u4F1A\u53D1\u73B0\u81EA\u5DF1\u80FD\u6BD4\u8BA1\u7B97\u673A\u505A\u7684\u66F4\u597D\u7684\u4E8B\u60C5\u6240\u5269\u4E0D\u591A\u4E86\u3002</p><h2 id="\u5982\u4F55\u6210\u4E3A2-\u7684\u4EBA" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u6210\u4E3A2-\u7684\u4EBA" aria-hidden="true">#</a> \u5982\u4F55\u6210\u4E3A2%\u7684\u4EBA</h2><p>\u4E0D\u4E00\u5B9A\u975E\u5F97\u53BB\u627E\u5927\u6570\u636E\u76F8\u5173\u7684\u5DE5\u4F5C\uFF0C\u91CD\u8981\u7684\u662F\u5229\u7528\u597D\u5927\u6570\u636E\u548C\u673A\u5668\u667A\u80FD\u3002</p><p>\u6BD4\u5982\u5728\u9152\u5427\u3001\u5546\u573A\u4F7F\u7528\u4F20\u611F\u5668\u76D1\u63A7\u6BCF\u4E00\u79CD\u5546\u54C1\u7684\u5177\u4F53\u60C5\u51B5\uFF0C\u7136\u540E\u901A\u8FC7\u5206\u6790\u5236\u5B9A\u5177\u4F53\u9500\u552E\u7B56\u7565\u4EE5\u63D0\u9AD8\u9500\u552E\u989D\u3002</p><p>\u751F\u6D3B\u662F\u5177\u4F53\u7684\uFF01\u5728\u672A\u6765\u53EF\u4EE5\u901A\u8FC7\u5927\u6570\u636E\u548C\u673A\u5668\u667A\u80FD\u6765\u5BF9\u5F88\u591A\u4E8B\u7269\u8FDB\u884C\u975E\u5E38\u7CBE\u7EC6\u5316\u7684\u76D1\u63A7\uFF0C\u4F7F\u7528\u597D\u8FD9\u4E9B\u6570\u636E\u3002</p><h2 id="\u5176\u4ED6" tabindex="-1"><a class="header-anchor" href="#\u5176\u4ED6" aria-hidden="true">#</a> \u5176\u4ED6</h2><ol><li><p>\u51E0\u5343\u5E74\u6765\uFF0C\u6211\u4EEC\u4EBA\u7C7B\u7684\u77E5\u8BC6\u90FD\u662F\u5EFA\u7ACB\u5728\u5F52\u7EB3\u6CD5\u4E4B\u4E0A\uFF0C\u5F52\u7EB3\u6CD5\u9690\u542B\u7684\u5047\u8BBE\u662F\u201C\u672A\u6765\u5C06\u7EE7\u7EED\u548C\u8FC7\u53BB\u4E00\u6837\u201D\uFF0C\u6362\u53E5\u8BDD\u8BF4\u5E94\u8BE5\u53EB\u8FDE\u7EED\u6027\u5047\u8BBE\u3002\u4F46\u5373\u5C06\u5230\u6765\u7684\u667A\u80FD\u65F6\u4EE3\uFF0C\u53EF\u4EE5\u8BF4\u4EBA\u7C7B\u5C06\u906D\u9047\u524D\u6240\u672A\u6709\u7684\u201C\u4E0D\u8FDE\u7EED\u6027\u201D\u3002\u5982\u4F55\u5728\u65B0\u7684\u65F6\u4EE3\u91CC\u751F\u5B58\uFF0C\u8DE8\u8D8A\u5E95\u5C42\u8BA4\u77E5\u7684\u4E0D\u8FDE\u7EED\u6027\uFF0C\u662F\u524D\u8FDB\u7684\u7B2C\u4E00\u6B65\u3002</p></li><li><p>\u672A\u6765\u7684\u793E\u4F1A\uFF0C\u5C5E\u4E8E\u5177\u6709\u521B\u610F\u7684\u4EBA\uFF0C\u800C\u4E0D\u5C5E\u4E8E\u638C\u63E1\u67D0\u79CD\u6280\u80FD\u505A\u91CD\u590D\u6027\u5DE5\u4F5C\u7684\u4EBA\u3002</p></li><li><p>\u5728\u725B\u987F\u4E4B\u524D\uFF0C\u4EBA\u7C7B\u5BF9\u4E8E\u81EA\u5DF1\u80FD\u5426\u8BA4\u8BC6\u81EA\u7136\u662F\u7F3A\u4E4F\u4FE1\u5FC3\u7684\uFF0C\u90A3\u4E9B\u6211\u4EEC\u4ECA\u5929\u770B\u4F3C\u4E0D\u9700\u8981\u89E3\u91CA\u7684\u81EA\u7136\u73B0\u8C61\uFF0C\u6BD4\u5982\u82F9\u679C\u4E3A\u4EC0\u4E48\u4F1A\u843D\u5730\uFF0C\u65E5\u6708\u661F\u8FB0\u4E3A\u4EC0\u4E48\u5347\u8D77\u53C8\u843D\u4E0B\uFF0C\u5728\u5F53\u65F6\u662F\u65E0\u6CD5\u88AB\u4EBA\u4EEC\u8BA4\u8BC6\u7684\uFF0C\u56E0\u6B64\u4EBA\u7C7B\u5BF9\u81EA\u7136\u6050\u60E7\u800C\u8FF7\u4FE1\u3002</p></li><li><p>\u5927\u6570\u636E\u65F6\u4EE3\u7528\u5F3A\u76F8\u5173\u6027\u66FF\u4EE3\u56E0\u679C\u5173\u7CFB\u3002</p></li><li><p>\u5728\u6D77\u91CF\u6570\u636E\u4E2D\u627E\u5230\u6570\u636E\u7684\u76F8\u5173\u6027\u3002</p></li><li><p>\u5B9E\u65F6\u6027\u3001\u4E2A\u6027\u5316\uFF08\u4F9D\u8D56\u4E8E\u5927\u91CF\u4E14\u5168\u9762\u7684\u7528\u6237\u6570\u636E\uFF09\u670D\u52A1\u80FD\u591F\u5E26\u6765\u5DE8\u5927\u7684\u7ADE\u4E89\u4F18\u52BF\uFF0C\u5927\u6570\u636E\u548C\u53EA\u80FD\u6280\u672F\u7ED3\u5408\u540E\uFF0C\u5176\u5B9E\u53EF\u4EE5\u5728\u4E00\u4E9B\u573A\u5408\u5E2E\u52A9\u4EBA\u505A\u51B3\u5B9A\u3002\u4E9A\u9A6C\u900A\u548C\u963F\u91CC\u5DF4\u5DF4\u5E2E\u52A9\u4EBA\u51B3\u5B9A\u4E86\u8BE5\u4E70\u4EC0\u4E48\u4E1C\u897F\uFF0C\u5948\u98DE\u5E2E\u52A9\u4EBA\u51B3\u7B56\u5E94\u8BE5\u770B\u4EC0\u4E48\u7535\u5F71\uFF0C\u800C\u8C37\u6B4C\u5219\u5E2E\u52A9\u4EBA\u51B3\u5B9A\u8BE5\u63A5\u53D7\u4EC0\u4E48\u4FE1\u606F\u3002</p></li><li><p>\u5927\u6570\u636E\u53EF\u4EE5\u8BA9\u5546\u4E1A\u884C\u4E3A\u5728\u51C6\u786E\u628A\u63A7\u5B8F\u89C2\u89C4\u5F8B\u7684\u540C\u65F6\uFF0C\u7CBE\u786E\u5230\u6BCF\u4E00\u4E2A\u7EC6\u8282\uFF0C\u4ECE\u800C\u63D0\u9AD8\u5229\u6DA6\u3002</p></li><li><p>\u5176\u5B9E\uFF0C\u4ECA\u5929\u6BCF\u4E00\u5BB6\u4F01\u4E1A\u90FD\u5E94\u8BE5\u628A\u81EA\u5DF1\u5F53\u6210\u5927\u6570\u636E\u4F01\u4E1A\uFF0C\u53EA\u8981\u6709\u5FC3\uFF0C\u83B7\u53D6\u6570\u636E\u5E76\u4E0D\u662F\u4EC0\u4E48\u96BE\u4E8B\uFF0C\u5373\u4F7F\u5728\u6240\u8C13\u7684\u4F20\u7EDF\u884C\u4E1A\u91CC\u3002\u5927\u6570\u636E\u53CC\u5411\u6D41\u52A8\u7684\u7279\u6027\uFF08\u6BCF\u4E00\u4E2A\u7EC6\u8282\u5230\u6574\u4F53\uFF1B\u4ECE\u6574\u4F53\u5230\u6BCF\u4E00\u4E2A\u7EC6\u8282[\u6570\u636E\u4ECE\u4E2A\u4F53\u4E2D\u6765\uFF0C\u7ECF\u8FC7\u6574\u5408\u53D8\u6210\u6709\u7528\u7684\u4FE1\u606F\u53CD\u9988\u5230\u4E2A\u4F53\u4E2D\u53BB]\uFF09\uFF0C\u7CBE\u786E\u5230\u5546\u4E1A\u7684\u6BCF\u4E00\u4E2A\u7EC6\u8282\uFF0C\u5E76\u4E14\u7531\u6B64\u5C06\u539F\u6765\u7684\u4E00\u9524\u5B50\u4E70\u5356\u53D8\u6210\u7EC6\u6C34\u957F\u6D41\u7684\u751F\u610F\uFF1B\u4ECE\u5546\u4E1A\u6A21\u5F0F\u4E0A\u8BB2\uFF0C\u5219\u662F\u5C06\u9500\u552E\u53D8\u4E3A\u670D\u52A1\u3002</p></li><li><p>\u5404\u79CD\u670D\u52A1\u5728\u4FE1\u606F\u9769\u547D\u540E\u53D8\u5F97\u8D8A\u6765\u8D8A\u91CD\u8981\u3002</p></li><li><p>\u793E\u4F1A\u516C\u5E73\u53EA\u4F53\u73B0\u5728\u673A\u4F1A\u5747\u7B49\u800C\u4E0D\u662F\u7ED3\u679C\u5747\u7B49\u4E0A\u3002</p></li></ol>',55),r=[h];function d(t,n){return a(),p("div",null,r)}const s=i(l,[["render",d],["__file","\u667A\u80FD\u65F6\u4EE3.html.vue"]]);export{s as default};
