import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as d,c as a,f as i}from"./app.17eb5f9b.js";const r={},c=i('<h2 id="\u672C\u7AE0\u8981\u70B9" tabindex="-1"><a class="header-anchor" href="#\u672C\u7AE0\u8981\u70B9" aria-hidden="true">#</a> \u672C\u7AE0\u8981\u70B9</h2><ol><li>\u4E92\u8054\u7F51\u8FB9\u7F18\u90E8\u5206\u548C\u6838\u5FC3\u90E8\u5206\u7684\u4F5C\u7528\u3002</li><li>\u8BA1\u7B97\u673A\u7F51\u7EDC\u7684\u6027\u80FD\u6307\u6807\u3002</li><li>\u8BA1\u7B97\u673A\u7F51\u7EDC\u5206\u5C42\u6B21\u7684\u4F53\u7CFB\u7ED3\u6784\u3002</li></ol><h2 id="\u6458\u8981" tabindex="-1"><a class="header-anchor" href="#\u6458\u8981" aria-hidden="true">#</a> \u6458\u8981</h2><ol><li>\u9AD8\u901F\u94FE\u8DEF\u63D0\u9AD8\u7684\u4EC5\u4EC5\u662F\u6570\u636E\u7684\u53D1\u9001\u901F\u7387\u800C\u4E0D\u662F\u6BD4\u7279\u5728\u94FE\u8DEF\u4E0A\u7684\u4F20\u64AD\u901F\u7387\u3002</li><li>\u8377\u8F7D\u4FE1\u606F\u7684\u7535\u78C1\u6CE2\u5728\u901A\u4FE1\u7EBF\u8DEF\u4E0A\u7684\u4F20\u64AD\u901F\u7387\u53D6\u51B3\u4E8E\u901A\u4FE1\u7EBF\u8DEF\u7684\u4ECB\u8D28\u6750\u6599\u3002</li><li>\u7535\u78C1\u6CE2\u5728\u5149\u7EBF\u4E2D\u7684\u4F20\u64AD\u901F\u7387\u4E3A <code>2 * 10^5 km/s</code>\uFF0C1000km \u957F\u7684\u5149\u7EBF\u7EBF\u8DEF\u4EA7\u751F\u7684\u4F20\u64AD\u65F6\u5EF6\u5927\u6982\u662F <code>5ms</code>\u3002</li><li>\u8BA1\u7B97\u673A\u7F51\u7EDC\u7684\u5404\u5C42\u53CA\u5176\u534F\u8BAE\u7684\u96C6\u5408\u5C31\u662F\u7F51\u7EDC\u7684\u4F53\u7CFB\u7ED3\u6784\u3002</li><li>\u4E92\u8054\u7F51\u7684\u4E24\u4E2A\u57FA\u672C\u7279\u70B9\uFF1A\u8FDE\u901A\u6027\u548C\u5171\u4EAB\u3002</li></ol><h2 id="\u4E92\u8054\u7F51\u7684\u53D1\u5C55" tabindex="-1"><a class="header-anchor" href="#\u4E92\u8054\u7F51\u7684\u53D1\u5C55" aria-hidden="true">#</a> \u4E92\u8054\u7F51\u7684\u53D1\u5C55</h2><h3 id="\u4E92\u8054\u7F51\u7684\u8BDE\u751F" tabindex="-1"><a class="header-anchor" href="#\u4E92\u8054\u7F51\u7684\u8BDE\u751F" aria-hidden="true">#</a> \u4E92\u8054\u7F51\u7684\u8BDE\u751F</h3><ul><li><code>1969</code> \u5E74\u7F8E\u56FD\u56FD\u9632\u90E8\u521B\u5EFA\u4E86\u7B2C\u4E00\u4E2A\u5206\u7EC4\u4EA4\u6362\u7F51 <code>ARPANET</code></li><li>20 \u4E16\u7EAA 70 \u5E74\u4EE3\uFF0C <code>ARPA</code> \u5F00\u59CB\u7814\u7A76\u591A\u79CD\u7F51\u7EDC\u4E92\u8FDE\u7684\u6280\u672F\uFF0C\u8FD9\u5BFC\u81F4\u4E86\u4E92\u8FDE\u7F51\u7EDC\u7684\u51FA\u73B0\uFF0C\u5F62\u6210\u4E86\u4E92\u8054\u7F51\u7684\u96CF\u5F62\u3002</li><li><code>1983</code> \u5E74 <code>TCP/IP</code> \u534F\u8BAE\u6210\u4E3A <code>ARPANET</code> \u7684\u6807\u51C6\u534F\u8BAE\uFF0C\u6240\u6709\u4F7F\u7528 <code>TCP/IP</code> \u534F\u8BAE\u7684\u8BA1\u7B97\u673A\u90FD\u80FD\u5229\u7528\u4E92\u8FDE\u7F51\u4E92\u76F8\u901A\u4FE1\uFF0C<code>1983</code> \u5E74\u88AB\u8BA4\u4E3A\u662F\u4E92\u8054\u7F51\u7684\u8BDE\u751F\u65F6\u95F4\u3002</li><li><code>1990</code> \u5E74 <code>ARPANET</code> \u6B63\u5F0F\u5BA3\u5E03\u5173\u95ED\u3002</li></ul><h3 id="\u4E92\u8054\u7F51\u57FA\u7840\u7ED3\u6784\u7684\u4E09\u4E2A\u53D1\u5C55\u9636\u6BB5" tabindex="-1"><a class="header-anchor" href="#\u4E92\u8054\u7F51\u57FA\u7840\u7ED3\u6784\u7684\u4E09\u4E2A\u53D1\u5C55\u9636\u6BB5" aria-hidden="true">#</a> \u4E92\u8054\u7F51\u57FA\u7840\u7ED3\u6784\u7684\u4E09\u4E2A\u53D1\u5C55\u9636\u6BB5</h3><ul><li><p>\u4ECE\u5355\u4E2A\u7F51\u7EDC <code>ARPANET</code> \u5411\u4E92\u8FDE\u7F51\u53D1\u5C55\uFF0C<a href="/document/%E8%AE%A1%E7%A1%95408/%E5%9F%BA%E7%A1%80%E7%BB%BC%E5%90%88/%E8%AE%A1%E7%AE%97%E6%9C%BA%E7%BD%91%E7%BB%9C/%E8%AE%A1%E7%AE%97%E6%9C%BA%E7%BD%91%E7%BB%9C%EF%BC%88%E7%AC%AC7%E7%89%88%EF%BC%89-%E8%B0%A2%E5%B8%8C%E4%BB%81/%E6%A6%82%E8%BF%B0?id=%E4%BA%92%E8%81%94%E7%BD%91%E7%9A%84%E8%AF%9E%E7%94%9F">\u4E92\u8054\u7F51\u7684\u8BDE\u751F</a>\u3002</p></li><li><p>\u5EFA\u6210\u4E86\u4E09\u7EA7\u7ED3\u6784\u7684\u4E92\u8054\u7F51\u3002</p><p><code>1985</code> \u5E74\u8D77\uFF0C\u7F8E\u56FD\u56FD\u5BB6\u79D1\u5B66\u57FA\u91D1\u4F1A <code>NFS</code> \u5C31\u56F4\u7ED5\u516D\u4E2A\u5927\u578B\u8BA1\u7B97\u673A\u4E2D\u5FC3\u5EFA\u8BBE\u8BA1\u7B97\u673A\u7F51\u7EDC\uFF0C\u5373\u56FD\u5BB6\u79D1\u5B66\u57FA\u91D1\u7F51 <code>NSFNET</code>\u3002\u5B83\u662F\u4E00\u4E2A\u4E09\u7EA7\u8BA1\u7B97\u673A\u7F51\u7EDC\uFF0C\u5206\u4E3A\u4E3B\u5E72\u7F51\u3001\u5730\u533A\u7F51\u548C\u6821\u56ED\u7F51\u3002<code>1991</code> \u5E74\u7F8E\u56FD\u653F\u5E9C\u5C06\u4E3B\u5E72\u7F51\u8F6C\u4EA4\u7ED9\u79C1\u4EBA\u516C\u53F8\u6765\u8FD0\u8425\u3002</p></li><li><p>\u9010\u6E10\u5F62\u6210\u4E86\u591A\u5C42\u6B21 <code>ISP</code> \u7ED3\u6784\u7684\u4E92\u8054\u7F51\u3002</p><p><code>1993</code> \u5E74\u5F00\u59CB\uFF0C\u7531\u7F8E\u56FD\u653F\u5E9C\u8D44\u52A9\u7684 <code>NSFNET</code> \u9010\u6E10\u88AB\u82E5\u5E72\u4E2A\u5546\u7528\u7684\u4E92\u8054\u7F51\u4E3B\u5E72\u7F51\u66FF\u4EE3\u3002\u73B0\u5728\u7684\u4E92\u8054\u7F51\u5DF2\u4E0D\u662F\u67D0\u4E2A\u5355\u4E2A\u7EC4\u7EC7\u6240\u62E5\u6709\u800C\u662F\u5168\u4E16\u754C\u5927\u5927\u5C0F\u5C0F\u7684 <a href="/document/%E8%AE%A1%E7%A1%95408/%E5%9F%BA%E7%A1%80%E7%BB%BC%E5%90%88/%E8%AE%A1%E7%AE%97%E6%9C%BA%E7%BD%91%E7%BB%9C/%E8%AE%A1%E7%AE%97%E6%9C%BA%E7%BD%91%E7%BB%9C%EF%BC%88%E7%AC%AC7%E7%89%88%EF%BC%89-%E8%B0%A2%E5%B8%8C%E4%BB%81/%E6%A6%82%E8%BF%B0?id=isp">ISP</a> \u6240\u5171\u540C\u62E5\u6709\u3002</p></li></ul><h2 id="\u4E92\u8054\u7F51\u7684\u7EC4\u6210" tabindex="-1"><a class="header-anchor" href="#\u4E92\u8054\u7F51\u7684\u7EC4\u6210" aria-hidden="true">#</a> \u4E92\u8054\u7F51\u7684\u7EC4\u6210</h2><p>\u6309\u7167\u5DE5\u4F5C\u65B9\u5F0F\u53EF\u5C06\u4E92\u8054\u7F51\u5206\u4E3A\u4E24\u90E8\u5206\uFF1A</p><ul><li>\u8FB9\u7F18\u90E8\u5206\uFF1A\u6240\u6709\u8FDE\u63A5\u5728\u4E92\u8054\u7F51\u7684\u4E3B\u673A\uFF0C\u5373\u5BA2\u6237\u7AEF\u3002</li><li>\u6838\u5FC3\u90E8\u5206\uFF1A\u7531\u5927\u91CF\u7F51\u7EDC\u548C\u8DEF\u7531\u5668\u7EC4\u6210\uFF0C\u6838\u5FC3\u90E8\u5206\u4E3A\u8FB9\u7F18\u90A3\u90E8\u5206\u63D0\u4F9B\u670D\u52A1\uFF08\u8FDE\u901A\u6027\u548C\u4EA4\u6362\uFF09\u3002</li></ul><h3 id="\u8FB9\u7F18\u90E8\u5206" tabindex="-1"><a class="header-anchor" href="#\u8FB9\u7F18\u90E8\u5206" aria-hidden="true">#</a> \u8FB9\u7F18\u90E8\u5206</h3><p>\u5728\u7AEF\u7CFB\u7EDF\u4E4B\u95F4\u8C03\u7684\u901A\u4FE1\u65B9\u5F0F\u901A\u5E38\u53EF\u5206\u4E3A\u4E24\u5927\u7C7B\uFF1A\u5BA2\u6237-\u670D\u52A1\u5668\u65B9\u5F0F\uFF08C/S\uFF09\u548C\u5BF9\u7B49\u65B9\u5F0F\uFF08P2P\uFF09\u3002</p><ul><li><p>\u5BF9\u7B49\u8FDE\u63A5 P2P(peer-to-peer)</p><p>\u901A\u4FE1\u53CC\u65B9\u4E0D\u533A\u5206\u8C01\u662F\u5BA2\u6237\u7AEF\u8C01\u662F\u670D\u52A1\u7AEF\uFF0C\u53EA\u8981\u4E24\u8005\u90FD\u8FD0\u884C\u4E86 P2P \u8F6F\u4EF6\uFF0C\u5C31\u53EF\u4EE5\u8FDB\u884C\u5E73\u7B49\u7684\u8FDE\u63A5\u901A\u4FE1\u3002\u8FD9\u65F6\u53CC\u65B9\u90FD\u53EF\u4EE5\u4E0B\u8F7D\u5BF9\u65B9\u5DF2\u7ECF\u5B58\u50A8\u5728\u786C\u76D8\u4E2D\u7684\u5171\u4EAB\u6587\u6863\u3002</p><p>\u672C\u8D28\u4E0A\u8BB2\uFF0C\u5BF9\u7B49\u8FDE\u63A5\u65B9\u5F0F\u4F9D\u7136\u662F\u4F7F\u7528 C/S \u6A21\u5F0F\uFF0C\u77E5\u8BC6\u5BF9\u7B49\u8FDE\u63A5\u4E2D\u7684\u6BCF\u4E00\u53F0\u4E3B\u673A\u65E2\u662F\u670D\u52A1\u7AEF\u53C8\u662F\u5BA2\u6237\u7AEF\u3002</p></li></ul><h3 id="\u6838\u5FC3\u90E8\u5206-\u5206\u7EC4\u4EA4\u6362" tabindex="-1"><a class="header-anchor" href="#\u6838\u5FC3\u90E8\u5206-\u5206\u7EC4\u4EA4\u6362" aria-hidden="true">#</a> \u6838\u5FC3\u90E8\u5206-\u5206\u7EC4\u4EA4\u6362</h3><p>\u4E92\u8054\u7F51\u6838\u5FC3\u90E8\u5206\u7684\u4E00\u4E2A\u91CD\u8981\u529F\u80FD\u662F\u5206\u7EC4\u4EA4\u6362\u3002</p><p>\u76F8\u6BD4\u4E8E<a href="/document/%E8%AE%A1%E7%A1%95408/%E5%9F%BA%E7%A1%80%E7%BB%BC%E5%90%88/%E8%AE%A1%E7%AE%97%E6%9C%BA%E7%BD%91%E7%BB%9C/%E8%AE%A1%E7%AE%97%E6%9C%BA%E7%BD%91%E7%BB%9C%EF%BC%88%E7%AC%AC7%E7%89%88%EF%BC%89-%E8%B0%A2%E5%B8%8C%E4%BB%81/%E6%A6%82%E8%BF%B0?id=%e7%94%b5%e8%b7%af%e4%ba%a4%e6%8d%a2">\u7535\u8DEF\u4EA4\u6362</a>\uFF0C\u5206\u7EC4\u4EA4\u6362\u7684\u65B9\u5F0F\u66F4\u9002\u5408\u4E92\u8054\u7F51\u3002</p><p>\u5206\u7EC4\u662F\u4E92\u8054\u7F51\u4E2D\u4F20\u9001\u7684\u6570\u636E\u5355\u5143\uFF0C\u5728\u5206\u7EC4\u4EA4\u6362\u4E2D\u5C06\u8981\u53D1\u9001\u7684\u6574\u4E2A\u6570\u636E\u5757\u79F0\u4E3A\u62A5\u6587\uFF0C\u5728\u53D1\u9001\u4E4B\u524D\u4F1A\u5C06\u62A5\u6587\u5212\u5206\u4E3A\u4E00\u4E2A\u4E2A\u7B49\u957F\u7684\u5C0F\u6570\u636E\u5757\uFF0C\u62A5\u6587\u5934\u90E8\u4F1A\u5B58\u50A8\u76EE\u7684\u5730\u5740\u548C\u6E90\u5730\u5740\u7B49\u91CD\u8981\u7684\u63A7\u5236\u4FE1\u606F\u3002</p><p>\u5206\u7EC4\u4EA4\u6362\u91C7\u7528\u5B58\u50A8\u8F6C\u53D1\u6280\u672F\uFF1A\u8DEF\u7531\u5668\u6536\u5230\u4E00\u4E2A\u5206\u7EC4\uFF0C\u5148\u6682\u65F6\u5B58\u50A8\u4E00\u4E0B\uFF0C\u68C0\u67E5\u5176\u9996\u90E8\uFF0C\u67E5\u627E\u8F6C\u53D1\u8868\uFF0C\u6309\u7167\u9996\u90E8\u4E2D\u7684\u76EE\u7684\u5730\u5740\uFF0C\u627E\u5230\u5408\u9002\u7684\u63A5\u53E3\u8F6C\u53D1\u51FA\u53BB\uFF0C\u628A\u5206\u7EC4\u4EA4\u7ED9\u4E0B\u4E00\u4E2A\u8DEF\u7531\u5668\uFF0C\u76F4\u5230\u5C06\u5206\u7EC4\u4EA4\u4ED8\u5230\u76EE\u6807\u4E3B\u673A\u3002</p><p>\u5404\u8DEF\u7531\u5668\u4E4B\u95F4\u5FC5\u987B\u7ECF\u5E38\u4EA4\u6362\u5F7C\u6B64\u638C\u63E1\u7684\u8DEF\u7531\u4FE1\u606F\uFF0C\u4EE5\u4FBF\u521B\u5EFA\u548C\u52A8\u6001\u7EF4\u62A4\u8DEF\u7531\u5668\u4E2D\u7684\u8F6C\u53D1\u8868\uFF0C\u4F7F\u8F6C\u53D1\u8868\u80FD\u591F\u53CA\u65F6\u66F4\u65B0\u3002</p><p>\u91C7\u7528\u5B58\u50A8\u8F6C\u53D1\u7684\u5206\u7EC4\u4EA4\u6362\uFF0C\u5B9E\u9645\u4E0A\u662F\u91C7\u7528\u4E86\u5728\u6570\u636E\u901A\u4FE1\u8FC7\u7A0B\u4E2D\u65AD\u7EED\u5206\u914D\u4F20\u8F93\u5E26\u5BBD\u7684\u7B56\u7565\u3002</p><h2 id="\u540D\u8BCD\u53CA\u6982\u5FF5" tabindex="-1"><a class="header-anchor" href="#\u540D\u8BCD\u53CA\u6982\u5FF5" aria-hidden="true">#</a> \u540D\u8BCD\u53CA\u6982\u5FF5</h2><h3 id="\u4E3B\u673A" tabindex="-1"><a class="header-anchor" href="#\u4E3B\u673A" aria-hidden="true">#</a> \u4E3B\u673A</h3><p>\u4E0E\u7F51\u7EDC\u76F8\u8FDE\u7684\u8BA1\u7B97\u673A\u5E38\u79F0\u4E3A\u4E3B\u673A\uFF08host\uFF09\u3002</p><p>\u8FDE\u63A5\u5728\u8BA1\u7B97\u673A\u7F51\u7EDC\u4E0A\u7684\u667A\u80FD\u624B\u673A\u4E5F\u53EF\u4EE5\u8BA4\u4E3A\u662F\u4E3B\u673A\u3002</p><h3 id="internet-\u4E92\u8FDE\u7F51" tabindex="-1"><a class="header-anchor" href="#internet-\u4E92\u8FDE\u7F51" aria-hidden="true">#</a> internet \u4E92\u8FDE\u7F51</h3><p>\u6CDB\u6307\u7531\u591A\u4E2A\u8BA1\u7B97\u673A\u7F51\u7EDC\u7EC4\u6210\u7684\u66F4\u5927\u7684\u8BA1\u7B97\u673A\u7F51\u7EDC\uFF0C\u8FD9\u4E9B\u7F51\u7EDC\u4E4B\u95F4\u7684\u901A\u4FE1\u534F\u8BAE\u53EF\u4EE5\u4EFB\u610F\u9009\u62E9\uFF0C\u4E0D\u4E00\u5B9A\u975E\u5F97\u662F <code>TCP/IP</code> \u534F\u8BAE\u3002</p><p>\u8BB8\u591A\u8BA1\u7B97\u673A\u8FDE\u63A5\u5728\u4E00\u8D77\u5F62\u6210\u8BA1\u7B97\u673A\u7F51\u7EDC\uFF0C\u4E92\u8FDE\u7F51\uFF08\u6CE8\u610F\u540D\u8BCD\uFF09\u5219\u628A\u8BB8\u591A\u8BA1\u7B97\u673A\u7F51\u7EDC\u901A\u8FC7\u8DEF\u7531\u5668\u8FDE\u63A5\u5728\u4E00\u8D77\u5F62\u6210\u66F4\u5927\u7684\u8BA1\u7B97\u673A\u7F51\u7EDC\u3002</p><h3 id="internet-\u4E92\u8054\u7F51" tabindex="-1"><a class="header-anchor" href="#internet-\u4E92\u8054\u7F51" aria-hidden="true">#</a> Internet \u4E92\u8054\u7F51</h3><p>\u6307\u5F53\u524D\u5168\u7403\u6700\u5927\u7684\u3001\u5F00\u653E\u7684\u3001\u7531\u4F17\u591A\u7F51\u7EDC\u4E92\u76F8\u8FDE\u63A5\u800C\u6210\u7684\u7279\u5B9A\u4E92\u8FDE\u7F51\uFF0C\u91C7\u7528 <code>TCP/IP</code> \u534F\u8BAE\u65CF\u4F5C\u4E3A\u6807\u51C6\u901A\u4FE1\u534F\u8BAE\uFF0C\u524D\u8EAB\u662F\u7F8E\u56FD\u7684 <code>ARPANET</code>\u3002</p><h3 id="isp" tabindex="-1"><a class="header-anchor" href="#isp" aria-hidden="true">#</a> ISP</h3><p><code>Internet Service Provider</code> \u4E92\u8054\u7F51\u670D\u52A1\u63D0\u4F9B\u8005/\u63D0\u4F9B\u5546\u3001\u7F51\u7EDC\u8FD0\u8425\u5546\u3002</p><p>\u5F88\u591A\u60C5\u51B5\u4E0B <code>ISP</code> \u5C31\u662F\u4E00\u4E2A\u8FDB\u884C\u5546\u4E1A\u6D3B\u52A8\u7684\u516C\u53F8\uFF0C\u5982\u4E2D\u56FD\u7535\u4FE1\u3001\u4E2D\u56FD\u8054\u901A\u548C\u4E2D\u56FD\u79FB\u52A8\u3002</p><p><code>ISP</code> \u53EF\u4EE5\u4ECE\u4E92\u8054\u7F51\u7BA1\u7406\u673A\u6784\u7533\u8BF7\u79DF\u8D41 <code>IP</code> \u5730\u5740\uFF0C\u540C\u65F6\u62E5\u6709\u901A\u4FE1\u7EBF\u8DEF\uFF08\u5927\u5382\u81EA\u5DF1\u9020\uFF0C\u5C0F\u5382\u5411\u5927\u5382\u79DF\uFF09\u53CA\u8DEF\u7531\u5668\u7B49\u8FDE\u7F51\u8BBE\u5907\uFF0C\u4EFB\u4F55\u673A\u6784\u548C\u4E2A\u4EBA\u53EA\u8981\u5411 <code>IPS</code> \u4ED8\u8D39\uFF0C\u5C31\u53EF\u4EE5\u4ECE\u8BE5 <code>ISP</code> \u83B7\u53D6 <code>IP</code> \u5730\u5740\u7684\u4F7F\u7528\u6743\uFF0C\u5E76\u53EF\u4EE5\u901A\u8FC7\u8BE5 <code>ISP</code> \u63A5\u5165\u5230\u4E92\u8054\u7F51\u3002</p><h3 id="\u7535\u8DEF\u4EA4\u6362" tabindex="-1"><a class="header-anchor" href="#\u7535\u8DEF\u4EA4\u6362" aria-hidden="true">#</a> \u7535\u8DEF\u4EA4\u6362</h3><p>\u4E00\u79CD\u4EA4\u6362\u65B9\u5F0F\uFF0C\u9700\u8981\u7ECF\u8FC7\u4E09\u4E2A\u6B65\u9AA4\uFF1A</p><ul><li>\u5EFA\u7ACB\u8FDE\u63A5</li><li>\u901A\u8BDD</li><li>\u91CA\u653E\u8FDE\u63A5</li></ul><p>\u7535\u8DEF\u4EA4\u6362\u7684\u91CD\u8981\u7279\u70B9\u662F\u5728\u901A\u8BDD\u7684\u5168\u90E8\u65F6\u95F4\u5185\uFF0C\u901A\u8BDD\u7684\u4E24\u4E2A\u7528\u6237\u59CB\u7EC8\u5360\u7528\u7AEF\u5230\u7AEF\u7684\u901A\u4FE1\u8D44\u6E90\u3002</p><h3 id="\u7F51\u7EDC\u534F\u8BAE" tabindex="-1"><a class="header-anchor" href="#\u7F51\u7EDC\u534F\u8BAE" aria-hidden="true">#</a> \u7F51\u7EDC\u534F\u8BAE</h3><p>\u4E3A\u8FDB\u884C\u7F51\u7EDC\u4E2D\u7684\u6570\u636E\u4EA4\u6362\u800C\u5EFA\u7ACB\u7684\u89C4\u5219\u3001\u6807\u51C6\u6216\u7EA6\u5B9A\u3002</p><p>\u7F51\u7EDC\u534F\u8BAE\u4E3B\u8981\u7531\u4E00\u4E0B\u4E09\u90E8\u5206\u7EC4\u6210\uFF1A</p><ul><li>\u8BED\u6CD5\uFF1A\u5373\u6570\u636E\u4E0E\u63A7\u5236\u4FE1\u606F\u7684\u7ED3\u6784\u6216\u683C\u5F0F\u3002</li><li>\u8BED\u4E49\uFF1A\u5373\u9700\u8981\u53D1\u51FA\u4F55\u79CD\u63A7\u5236\u4FE1\u606F\uFF0C\u5B8C\u6210\u4F55\u79CD\u52A8\u4F5C\u4EE5\u53CA\u505A\u51FA\u4F55\u79CD\u76F8\u5E94\u3002</li><li>\u540C\u6B65\uFF1A\u4E8B\u4EF6\u5B9E\u73B0\u987A\u5E8F\u7684\u8BE6\u7EC6\u8BF4\u660E\u3002</li></ul><h2 id="\u7591\u95EE" tabindex="-1"><a class="header-anchor" href="#\u7591\u95EE" aria-hidden="true">#</a> \u7591\u95EE</h2><h3 id="ip-\u5730\u5740\u662F\u7531\u4EC0\u4E48\u673A\u6784\u5206\u914D\u7684" tabindex="-1"><a class="header-anchor" href="#ip-\u5730\u5740\u662F\u7531\u4EC0\u4E48\u673A\u6784\u5206\u914D\u7684" aria-hidden="true">#</a> IP \u5730\u5740\u662F\u7531\u4EC0\u4E48\u673A\u6784\u5206\u914D\u7684?</h3><p>\u6240\u6709\u7684\u516C\u6709 IP \u5730\u5740\uFF08Public address\uFF09 \u90FD\u7531\u56FD\u9645\u7EC4\u7EC7 Inter NIC\uFF08Internet Network Information Center \u56E0\u7279\u7F51\u4FE1\u606F\u4E2D\u5FC3\uFF09\u8D1F\u8D23\u7EDF\u4E00\u5206\u914D\uFF0C\u76EE\u524D\u5168\u4E16\u754C\u5171\u6709\u4E09\u4E2A\u8FD9\u6837\u7684\u7F51\u7EDC\u4FE1\u606F\u4E2D\u5FC3\u3002\u8FD9\u4E9B IP \u5730\u5740\u5206\u914D\u7ED9\u6CE8\u518C\u5E76\u5411 Inter NIC \u63D0\u51FA\u7533\u8BF7\u7684\u7EC4\u7EC7\u673A\u6784\u3002\u901A\u8FC7\u5B83\u76F4\u63A5\u8BBF\u95EE\u56E0\u7279\u7F51\u3002</p><p>\u6211\u56FD\u7533\u8BF7 IP \u5730\u5740\u8981\u901A\u8FC7 APNIC\uFF0CAPNIC \u7684\u603B\u90E8\u8BBE\u5728\u65E5\u672C\u4E1C\u4EAC\u5927\u5B66\uFF0C\u540E\u4E8E 1998 \u5E74\u8FC1\u5230\u6FB3\u5927\u5229\u4E9A\u5E03\u91CC\u65AF\u73ED\u3002</p><h3 id="\u8DEF\u7531\u8868\u5B58\u50A8\u5728\u8DEF\u7531\u5668\u4E2D" tabindex="-1"><a class="header-anchor" href="#\u8DEF\u7531\u8868\u5B58\u50A8\u5728\u8DEF\u7531\u5668\u4E2D" aria-hidden="true">#</a> \u8DEF\u7531\u8868\u5B58\u50A8\u5728\u8DEF\u7531\u5668\u4E2D\uFF1F</h3><p>\u8DEF\u7531\u8868\u5B58\u50A8\u5728\u8DEF\u7531\u5668\u4E2D\u3002</p><p>\u8DEF\u7531\u8868\u53EF\u4EE5\u5206\u4E3A\u9759\u6001\u8DEF\u7531\u8868\u548C\u52A8\u6001\u8DEF\u7531\u8868\u3002</p><p>\u9759\u6001\u8DEF\u7531\u8868\u7531\u7BA1\u7406\u5458\u8BBE\u7F6E\uFF0C\u4E5F\u53EF\u4EE5\u901A\u8FC7\u4E3B\u673A\u63A7\u5236\u3002</p><p>\u52A8\u6001\u8DEF\u7531\u8868\u662F\u5728\u8DEF\u7531\u5668\u4E0A\u8FD0\u884C\u4E00\u4E2A\u8DEF\u7531\u534F\u8BAE\u7A0B\u5E8F\uFF0C\u8FD9\u4E2A\u7A0B\u5E8F\u4F1A\u6839\u636E\u5F53\u524D\u7684\u7F51\u7EDC\u62D3\u6251\u52A8\u6001\u4FEE\u6539\u8DEF\u7531\u8868\u3002</p>',52),h=[c];function o(E,l){return d(),a("div",null,h)}const p=e(r,[["render",o],["__file","\u6982\u8FF0.html.vue"]]);export{p as default};
