import{_ as a}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as h,f as r}from"./app.382dc3cd.js";const d={},p=r('<p>\u4F5C\u8005\uFF1A\u9A6C\u6B47\u5C14\xB7\u5362\u68EE\u5821</p><p>\u51FA\u7248\u793E\uFF1A\u534E\u590F\u51FA\u7248\u793E</p><p>\u51FA\u7248\u65E5\u671F\uFF1A2019 \u5E74 8 \u6708</p><h2 id="\u975E\u66B4\u529B\u6C9F\u901A\u7684\u56DB\u8981\u7D20" tabindex="-1"><a class="header-anchor" href="#\u975E\u66B4\u529B\u6C9F\u901A\u7684\u56DB\u8981\u7D20" aria-hidden="true">#</a> \u975E\u66B4\u529B\u6C9F\u901A\u7684\u56DB\u8981\u7D20</h2><ol><li>\u89C2\u5BDF\uFF1A\u5BA2\u89C2\u3001\u6E05\u695A\u5730\u9610\u8FF0\u4E8B\u4EF6\u672C\u8EAB\uFF0C\u4E0D\u8981\u5E26\u6709\u4E3B\u89C2\u60C5\u7EEA</li><li>\u611F\u53D7\uFF1A\u8868\u8FBE\u81EA\u5DF1\u5BF9\u6B64\u4E8B\u4EF6\u7684\u611F\u53D7</li><li>\u660E\u786E\u9700\u6C42\uFF1A\u8BF4\u51FA\u662F\u4EC0\u4E48\u5BFC\u81F4\u81EA\u5DF1\u4EA7\u751F\u8FD9\u6837\u7684\u611F\u53D7\uFF0C\u901A\u5E38\u662F\u81EA\u5DF1\u7684\u9700\u6C42\u6CA1\u6709\u88AB\u6EE1\u8DB3</li><li>\u8BF7\u6C42\uFF1A\u63D0\u51FA\u8BF7\u6C42\uFF0C\u660E\u786E\u7684\u544A\u8BC9\u5BF9\u65B9\u5982\u4F55\u6EE1\u8DB3\u6211\u4EEC\u7684\u9700\u6C42</li></ol><h3 id="\u89C2\u5BDF\u4E0E\u8BC4\u8BBA" tabindex="-1"><a class="header-anchor" href="#\u89C2\u5BDF\u4E0E\u8BC4\u8BBA" aria-hidden="true">#</a> \u89C2\u5BDF\u4E0E\u8BC4\u8BBA</h3><blockquote><p>\u4E0D\u5E26\u8BC4\u8BBA\u7684\u89C2\u5BDF\u662F\u4EBA\u7C7B\u667A\u6167\u7684\u6700\u9AD8\u5F62\u5F0F\u3002 \u2014\u2014\u5370\u5EA6\u54F2\u5B66\u5BB6 \u514B\u91CC\u5E0C\u90A3\u7A46\u63D0</p></blockquote><p>\u5C06\u89C2\u5BDF\u548C\u8BC4\u8BBA\u6DF7\u4E3A\u4E00\u8C08\uFF0C\u522B\u4EBA\u5C31\u4F1A\u503E\u5411\u4E8E\u542C\u5230\u6279\u8BC4\uFF0C\u5E76\u53CD\u9A73\u6211\u4EEC\u3002</p><p>\u6BD4\u5982\u6211\u8BF4\uFF1A\u201C\u4F60\u82B1\u94B1\u592A\u5927\u624B\u5927\u811A\u4E86\u201D\uFF0C\u8FD9\u53E5\u8BDD\u4E2D\u6CA1\u6709\u63D0\u5230\u6211\u201C\u4E3A\u4EC0\u4E48\u89C9\u5F97\u4F60\u82B1\u94B1\u5927\u624B\u5927\u811A\u201D\uFF0C\u7F3A\u4E4F\u4F9D\u636E\uFF0C\u628A\u81EA\u5DF1\u7684\u8BC4\u8BBA\u5F53\u505A\u4E86\u4E8B\u5B9E\u3002</p><p>\u6B64\u65F6\u5E94\u8BE5\u63D0\u4F9B\u7B2C\u4E00\u8981\u7D20\uFF0C\u4E5F\u5C31\u662F\u6211\u89C2\u5BDF\u5230\u7684\u4F60\u201C\u82B1\u94B1\u5927\u624B\u5927\u811A\u201D\u7684\u4E8B\u4EF6\u6216\u884C\u4E3A\u3002</p><p>\u4E0E\u4EBA\u6C9F\u901A\u65F6\u76F4\u63A5\u629B\u51FA\u81EA\u5DF1\u7684\u8BC4\u8BBA\u901A\u5E38\u4F1A\u5F15\u8D77\u5BF9\u65B9\u7684\u4E0D\u9002\uFF0C\u5982\u679C\u5BF9\u65B9\u7528\u8FD9\u79CD\u65B9\u5F0F\u4E0E\u6211\u4EEC\u6C9F\u901A\uFF0C\u6211\u4EEC\u5E94\u8BE5\u8BE2\u95EE\u5BF9\u65B9\u63D0\u51FA\u8FD9\u79CD\u8BC4\u8BBA\u7684\u539F\u56E0\uFF0C\u662F\u6211\u4EEC\u7684\u4EC0\u4E48\u884C\u4E3A\u5BFC\u81F4\u4ED6\u6709\u8FD9\u6837\u7684\u770B\u6CD5\u3002</p><h3 id="\u611F\u53D7\u548C\u9700\u6C42" tabindex="-1"><a class="header-anchor" href="#\u611F\u53D7\u548C\u9700\u6C42" aria-hidden="true">#</a> \u611F\u53D7\u548C\u9700\u6C42</h3><p>\u5728\u8868\u8FBE\u611F\u53D7\u65F6\uFF0C\u793A\u5F31\u6709\u52A9\u4E8E\u89E3\u51B3\u51B2\u7A81\u3002</p><p>\u8868\u8FBE\u611F\u53D7\u65F6\u5BF9\u4E8B\u4E0D\u5BF9\u4EBA\uFF0C\u8981\u6E05\u695A\u7684\u8868\u8FBE\u662F\u4EC0\u4E48\u5BFC\u81F4\u81EA\u5DF1\u4EA7\u751F\u8FD9\u6837\u7684\u611F\u53D7\u3002</p><p>\u4F8B-\u8868\u8FBE\u5931\u671B\u7684\u4E24\u79CD\u65B9\u5F0F\uFF1A</p><p>\u7532\uFF1A\u4F60\u6628\u665A\u6CA1\u6765\u4EE4\u6211\u5F88\u5931\u671B\u3002</p><p>\u4E59\uFF1A\u6628\u665A\u4F60\u6CA1\u6765\uFF0C\u6211\u5F88\u5931\u671B\uFF0C\u56E0\u4E3A\u6211\u60F3\u548C\u4F60\u8BF4\u4E00\u4E9B\u70E6\u5FC3\u4E8B\u3002</p><p>\u7532\u65B9\u5F0F\u8BA9\u522B\u4EBA\u4EE5\u4E3A\u662F\u4ED6\u7684\u884C\u4E3A\u4F7F\u4ED6\u4E0D\u9AD8\u5174\u3002</p><p>\u4E59\u65B9\u5F0F\u8BA9\u522B\u4EBA\u8BA4\u4E3A\u662F\u4ED6\u7684\u9700\u6C42\u6CA1\u6709\u88AB\u6EE1\u8DB3\u4F7F\u4ED6\u4E0D\u6109\u5FEB\u3002</p><p>\u4ED6\u4EBA\u7684\u8A00\u884C\u4E5F\u8BB8\u548C\u6211\u4EEC\u7684\u611F\u53D7\u6709\u5173\uFF0C\u4F46\u5E76\u4E0D\u662F\u6211\u4EEC\u611F\u53D7\u7684\u8D77\u56E0\uFF0C<strong>\u611F\u53D7\u8D77\u6E90\u4E8E\u6211\u4EEC\u81EA\u8EAB\u7684\u9700\u8981</strong>\u3002</p><h3 id="\u8BF7\u6C42\u548C\u53CD\u9988" tabindex="-1"><a class="header-anchor" href="#\u8BF7\u6C42\u548C\u53CD\u9988" aria-hidden="true">#</a> \u8BF7\u6C42\u548C\u53CD\u9988</h3><p>\u6211\u4EEC\u63D0\u51FA\u7684\u9700\u6C42\u8D8A\u5177\u4F53\u8D8A\u597D\u3002\u907F\u514D\u4F7F\u7528\u62BD\u8C61\u7684\u8BCD\u8BED\uFF0C\u6BD4\u5982\uFF1A\u5E0C\u671B\u201C\u516C\u5E73\u201D\u3001\u5E0C\u671B\u201C\u81EA\u7531\u201D\u3002</p><p>\u5BF9\u522B\u4EBA\u63D0\u51FA\u8981\u6C42\u65F6\u8981\u6307\u51FA\u5E0C\u671B\u5BF9\u65B9\u505A\u4EC0\u4E48\u800C\u4E0D\u662F\u5E0C\u671B\u5BF9\u65B9\u4E0D\u8981\u505A\u4EC0\u4E48\u3002\u8FD9\u6837\u8F83\u6709\u53EF\u80FD\u505A\u51FA\u79EF\u6781\u7684\u56DE\u5E94\u3002</p><p>\u6211\u4EEC\u7684\u610F\u601D\u548C\u522B\u4EBA\u7684\u7406\u89E3\u6709\u65F6\u53EF\u80FD\u662F\u4E24\u56DE\u4E8B\uFF0C\u4E86\u89E3\u5BF9\u65B9\u662F\u5426\u7406\u89E3\u6700\u597D\u7684\u65B9\u5F0F\u662F\u8BA9\u5BF9\u65B9\u5145\u5206\u8868\u8FF0\u4E00\u904D\u81EA\u5DF1\u7684\u7406\u89E3\u3002</p><h2 id="\u503E\u542C\u7684\u8981\u70B9" tabindex="-1"><a class="header-anchor" href="#\u503E\u542C\u7684\u8981\u70B9" aria-hidden="true">#</a> \u503E\u542C\u7684\u8981\u70B9</h2><h3 id="\u4F53\u4F1A\u4ED6\u4EBA\u7684\u611F\u53D7" tabindex="-1"><a class="header-anchor" href="#\u4F53\u4F1A\u4ED6\u4EBA\u7684\u611F\u53D7" aria-hidden="true">#</a> \u4F53\u4F1A\u4ED6\u4EBA\u7684\u611F\u53D7</h3><p>\u8BD5\u56FE\u5206\u6790\u95EE\u9898\u59A8\u788D\u4E86\u6211\u4EEC\u4E0E\u4ED6\u4EBA\u7684\u8054\u7CFB\u3002</p><p>\u5982\u679C\u6211\u4EEC\u53EA\u5173\u5FC3\u522B\u4EBA\u8BF4\u4E86\u4EC0\u4E48\uFF0C\u5E76\u8003\u8651\u4ED6\u7684\u60C5\u51B5\u7B26\u5408\u54EA\u79CD\u7406\u8BBA\uFF0C\u6211\u4EEC\u662F\u5728\u8BCA\u65AD\u4EBA\u2014\u2014\u6211\u4EEC\u5E76\u6CA1\u6709\u503E\u542C\u4ED6\u4EEC\u3002</p><p>\u5728\u975E\u66B4\u529B\u6C9F\u901A\u4E2D\uFF0C\u503E\u542C\u4ED6\u4EBA\u610F\u5473\u7740\uFF0C\u653E\u4E0B\u5DF2\u6709\u7684\u60F3\u6CD5\u548C\u5224\u65AD\uFF0C\u4E00\u5FC3\u4E00\u610F\u5730\u4F53\u4F1A\u4ED6\u4EBA\u3002</p><p>\u5982\u679C\u5BF9\u65B9\u7684\u8BDD\u8BED\u4E2D\u5E26\u7740\u5F3A\u70C8\u7684\u60C5\u7EEA\uFF0C\u4E0D\u8981\u628A\u4ED6\u7684\u8BDD\u770B\u4F5C\u662F\u5BF9\u6211\u7684\u653B\u51FB\uFF0C\u800C\u6CE8\u610F\u4F53\u4F1A\u4ED6\u7684\u611F\u53D7\u548C\u9700\u8981\u3002</p><h3 id="\u53CA\u65F6\u53CD\u9988" tabindex="-1"><a class="header-anchor" href="#\u53CA\u65F6\u53CD\u9988" aria-hidden="true">#</a> \u53CA\u65F6\u53CD\u9988</h3><p>\u5728\u503E\u542C\u4ED6\u4EBA\u7684\u89C2\u5BDF\u3001\u611F\u53D7\u3001\u9700\u8981\u548C\u8BF7\u6C42\u4E4B\u540E\uFF0C\u6211\u4EEC\u53EF\u4EE5\u4E3B\u52A8\u8868\u8FBE\u6211\u4EEC\u7684\u7406\u89E3\u3002\u5982\u679C\u6211\u4EEC\u5DF2\u7ECF\u51C6\u786E\u9886\u4F1A\u4E86\u4ED6\u4EEC\u7684\u610F\u601D\uFF0C\u6211\u4EEC\u7684\u53CD\u9988\u5C06\u5E2E\u52A9\u4ED6\u4EEC\u610F\u8BC6\u5230\u8FD9\u4E00\u70B9\u3002\u53CD\u4E4B\uFF0C\u5982\u679C\u6211\u4EEC\u7684\u7406\u89E3\u8FD8\u4E0D\u5230\u4F4D\uFF0C\u4ED6\u4EEC\u4E5F\u5C31\u6709\u673A\u4F1A\u6765\u7EA0\u6B63\u6211\u4EEC\u3002</p><p>\u5982\u679C\u6CA1\u6709\u5B8C\u5168\u7406\u89E3\u522B\u4EBA\u7684\u60F3\u6CD5\uFF0C\u53EF\u4EE5\u53D1\u51FA\u7591\u95EE\u5E76\u6839\u636E\u5F53\u524D\u7684\u7406\u89E3\u629B\u51FA\u4E00\u4E9B\u9009\u9879\uFF1A\u201C\u6211\u6709\u4E9B\u56F0\u60D1\u3002\u6211\u60F3\u77E5\u9053\u4F60\u662F\u6307\u54EA\u4EF6\u4E8B\u3002\u544A\u8BC9\u6211\u597D\u5417\uFF1F\u201D</p><h3 id="\u63D0\u51FA\u81EA\u5DF1\u7684\u7406\u89E3\u800C\u4E0D\u662F\u5355\u7EAF\u7684\u53D1\u95EE" tabindex="-1"><a class="header-anchor" href="#\u63D0\u51FA\u81EA\u5DF1\u7684\u7406\u89E3\u800C\u4E0D\u662F\u5355\u7EAF\u7684\u53D1\u95EE" aria-hidden="true">#</a> \u63D0\u51FA\u81EA\u5DF1\u7684\u7406\u89E3\u800C\u4E0D\u662F\u5355\u7EAF\u7684\u53D1\u95EE</h3><p>\u6CE8\u610F\u4E0B\u9762\u4E24\u53E5\u8BDD\u7684\u533A\u522B\uFF1A</p><p><code>\u201C\u4E0A\u5468\u6211\u6709\u4E09\u4E2A\u665A\u4E0A\u4E0D\u5728\u5BB6\uFF0C\u4F60\u8BF4\u7684\u662F\u8FD9\u56DE\u4E8B\uFF1F\u201D</code></p><p><code>\u201C\u4F60\u8BF4\u7684\u662F\u4EC0\u4E48\u4E8B\uFF1F\u201D</code></p><p>\u7B2C\u4E8C\u7EC4\u7684\u8FD9\u4E9B\u95EE\u9898\u770B\u8D77\u6765\u5F88\u76F4\u63A5\uFF0C\u4F46\u5B83\u4EEC\u5E76\u4E0D\u662F\u83B7\u5F97\u4FE1\u606F\u7684\u6700\u4F73\u65B9\u5F0F\u3002\u8FD9\u6837\u7684\u63D0\u95EE\u65B9\u5F0F\uFF0C\u5C31\u50CF\u5FC3\u7406\u54A8\u8BE2\u5E08\u5728\u5E2E\u52A9\u5BA2\u6237\uFF0C\u5BB9\u6613\u4EA7\u751F\u8DDD\u79BB\u611F\u3002</p><h3 id="\u629B\u5F03\u504F\u89C1" tabindex="-1"><a class="header-anchor" href="#\u629B\u5F03\u504F\u89C1" aria-hidden="true">#</a> \u629B\u5F03\u504F\u89C1</h3><p>\u5982\u679C\u6211\u4EEC\u6EE1\u8111\u5B50\u662F\u975E\u5BF9\u9519\uFF0C\u628A\u67D0\u4E9B\u4EBA\u770B\u4F5C\u662F\u8D2A\u5A6A\u7684\u4EBA\u3001\u4E0D\u8D1F\u8D23\u4EFB\u7684\u4EBA\u3001\u9A97\u5B50\u6216\u5176\u4ED6\u7C7B\u578B\u7684\u574F\u4EBA\uFF0C\u90A3\u4E48\uFF0C\u6211\u4EEC\u5C31\u5F88\u96BE\u4E0E\u4ED6\u4EEC\u5EFA\u7ACB\u826F\u597D\u7684\u5173\u7CFB\u3002</p><h2 id="\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u95EE\u9898" aria-hidden="true">#</a> \u95EE\u9898</h2><h3 id="\u4EC0\u4E48\u662F\u975E\u66B4\u529B\u6C9F\u901A" tabindex="-1"><a class="header-anchor" href="#\u4EC0\u4E48\u662F\u975E\u66B4\u529B\u6C9F\u901A" aria-hidden="true">#</a> \u4EC0\u4E48\u662F\u975E\u66B4\u529B\u6C9F\u901A\uFF1F</h3><p>\u8BF4\u8BDD\u7684\u65F6\u5019\u628A\u8BDD\u8BF4\u6E05\u695A\uFF0C\u4E0D\u8981\u53EA\u8BF4\u4E00\u534A\u8BA9\u522B\u4EBA\u53BB\u731C\u81EA\u5DF1\u7684\u771F\u5B9E\u60F3\u6CD5\uFF1B\u542C\u522B\u4EBA\u8BF4\u8BDD\u7684\u65F6\u5019\u53BB\u62C6\u89E3\u522B\u4EBA\u8BDD\u91CC\u7684\u4FE1\u606F\uFF0C\u907F\u514D\u4E89\u8BBA\uFF0C\u4ECE\u5BF9\u65B9\u7684\u8BDD\u91CC\u63D0\u53D6\u51FA\u4ED6\u8981\u8868\u8FBE\u4EC0\u4E48\u3002</p><p>\u5982\u679C\u6211\u4EEC\u901A\u8FC7\u6279\u8BC4\u63D0\u51FA\u4E3B\u5F20\uFF0C\u4EBA\u4EEC\u7684\u53CD\u5E94\u5E38\u5E38\u662F\u7533\u8FA9\u6216\u53CD\u51FB\u3002\u53CD\u4E4B\uFF0C\u5982\u679C\u6211\u4EEC\u76F4\u63A5\u8BF4\u51FA\u9700\u8981\uFF0C\u5176\u4ED6\u4EBA\u5C31\u8F83\u6709\u53EF\u80FD\u505A\u51FA\u79EF\u6781\u7684\u56DE\u5E94\u3002</p><h3 id="\u90A3\u4E48\u4EC0\u4E48\u662F\u66B4\u529B\u6C9F\u901A" tabindex="-1"><a class="header-anchor" href="#\u90A3\u4E48\u4EC0\u4E48\u662F\u66B4\u529B\u6C9F\u901A" aria-hidden="true">#</a> \u90A3\u4E48\u4EC0\u4E48\u662F\u66B4\u529B\u6C9F\u901A\uFF1F</h3>',45),i=[p];function n(t,c){return e(),h("div",null,i)}const l=a(d,[["render",n],["__file","\u975E\u66B4\u529B\u6C9F\u901A.html.vue"]]);export{l as default};
